    this.push(normals, vertexNormal3.z);

    // COLORS
    this.push(colors, color.r);
    this.push(colors, color.g);
    this.push(colors, color.b);

    this.push(colors, color.r);
    this.push(colors, color.g);
    this.push(colors, color.b);

    this.push(colors, color.r);
    this.push(colors, color.g);
    this.push(colors, color.b);

