AddedText.prototype.setShaderMargin = function(isMarginX, value){
  if (!this.mesh.material.uniforms.margin2D){
    this.mesh.material.uniforms.margin2D = new THREE.Uniform(new THREE.Vector2());
    this.mesh.material.needsUpdate = true;
  }
  if (isMarginX){
    this.shaderMargin.x = value;
    this.mesh.material.uniforms.margin2D.value.x = value;
  }else{
    this.shaderMargin.y = value;
    this.mesh.material.uniforms.margin2D.value.y = value;
  }
}

AddedText.prototype.setFog = function(){
  if (this.is2D || !this.isAffectedByFog){
    return;
  }
  if (!this.mesh.material.uniforms.fogInfo){
    macroHandler.injectMacro("HAS_FOG", this.material, false, true);
    this.mesh.material.uniforms.fogInfo = GLOBAL_FOG_UNIFORM;
  }
  if (fogHandler.isFogBlendingWithSkybox()){
    if (!this.mesh.material.uniforms.cubeTexture){
      macroHandler.injectMacro("HAS_SKYBOX_FOG", this.material, true, true);
      this.mesh.material.uniforms.worldMatrix = new THREE.Uniform(this.mesh.matrixWorld);
      this.mesh.material.uniforms.cubeTexture = GLOBAL_CUBE_TEXTURE_UNIFORM;
      this.mesh.material.uniforms.cameraPosition = GLOBAL_CAMERA_POSITION_UNIFORM;
    }
  }
  this.mesh.material.needsUpdate = true;
}

AddedText.prototype.removeFog = function(){
  if (this.is2D || !this.isAffectedByFog){
    return;
  }
  macroHandler.removeMacro("HAS_FOG", this.material, false, true);
  macroHandler.removeMacro("HAS_SKYBOX_FOG", this.material, true, true);
  delete this.mesh.material.uniforms.fogInfo;
  delete this.mesh.material.uniforms.cubeTexture;
  delete this.mesh.material.uniforms.worldMatrix;
  delete this.mesh.material.uniforms.cameraPosition;
  this.mesh.material.needsUpdate = true;
}

var AreaConfigurationsHandler = function(){
  this.sideAry = ["Both", "Front", "Back"];
  this.areaDefault = "default";
  this.updateNeeded = false;
}

AreaConfigurationsHandler.prototype.onCurrentAreaChange = function(enteredAreaName, exitedAreaName){
  if (mode == 0){
    return;
  }
  var enterCallbackFunc = areaEnterCallbacks[enteredAreaName];
  var exitCallbackFunc = areaExitCallbacks[exitedAreaName];
  if (enteredAreaName != this.areaDefault){
    if (enterCallbackFunc && areas[enteredAreaName].registeredSceneName == sceneHandler.getActiveSceneName()){
      enterCallbackFunc(exitedAreaName);
    }
  }else{
    if (enterCallbackFunc){
      enterCallbackFunc(exitedAreaName);
    }
  }
  if (exitedAreaName != this.areaDefault){
    if (exitCallbackFunc && areas[exitedAreaName].registeredSceneName == sceneHandler.getActiveSceneName()){
      exitCallbackFunc(enteredAreaName);
    }
  }else{
    if (exitCallbackFunc){
      exitCallbackFunc(enteredAreaName);
    }
  }
}

AreaConfigurationsHandler.prototype.onAfterSceneChange = function(){
  var result = sceneHandler.getAreaBinHandler().queryArea(camera.position);
  if (result){
    for (var objName in sceneHandler.getAddedObjects()){
      addedObjects[objName].applyAreaConfiguration(result);
    }
    for (var objName in sceneHandler.getObjectGroups()){
      objectGroups[objName].applyAreaConfiguration(result);
    }
    for (var textName in sceneHandler.getAddedTexts()){
      addedTexts[textName].applyAreaConfiguration(result);
    }
  }else{
    for (var objName in sceneHandler.getAddedObjects()){
      addedObjects[objName].applyAreaConfiguration("default");
    }
    for (var objName in sceneHandler.getObjectGroups()){
      objectGroups[objName].applyAreaConfiguration("default");
    }
    for (var textName in sceneHandler.getAddedTexts()){
      addedTexts[textName].applyAreaConfiguration("default");
    }
  }
}

AreaConfigurationsHandler.prototype.handle = function(){
  if (mode == 0 && !isDeployment){
    if (guiHandler.isOneOfBlockingGUIActive()){
      return;
    }
  }
  var result = sceneHandler.getAreaBinHandler().queryArea(camera.position);
  if (result){
    if (result != this.currentArea){
      this.onCurrentAreaChange(result, this.currentArea);
      this.currentArea = result;
      this.updateNeeded = true;
    }else{
      this.updateNeeded = false;
    }
  }else if (this.currentArea != this.areaDefault){
    this.onCurrentAreaChange(this.areaDefault, this.currentArea);
    this.currentArea = this.areaDefault;
    this.updateNeeded = true;
  }else{
    this.updateNeeded = false;
  }
}

AreaConfigurationsHandler.prototype.generateConfigurations = function(){
  this.visibilityConfigurations = new Object();
  this.sideConfigurations = new Object();

  for (var areaName in sceneHandler.getAreas()){
    this.visibilityConfigurations[areaName] = new Object();
    this.sideConfigurations[areaName] = new Object();
    for (var objName in sceneHandler.getAddedObjects()){
      var obj = addedObjects[objName];
      this.visibilityConfigurations[areaName][objName] = {
        "Visible": obj.getVisibilityInArea(areaName)
      };
      this.sideConfigurations[areaName][objName] = {
        "Side": obj.getSideInArea(areaName)
      };
    }
    for (var objName in sceneHandler.getObjectGroups()){
      var obj = objectGroups[objName];
      this.visibilityConfigurations[areaName][objName] = {
        "Visible": obj.getVisibilityInArea(areaName)
      };
      this.sideConfigurations[areaName][objName] = {
        "Side": obj.getSideInArea(areaName)
      };
    }
    for (var textName in sceneHandler.getAddedTexts()){
      var addedText = addedTexts[textName];
      if (addedText.is2D){
        continue;
      }
      this.visibilityConfigurations[areaName][textName] = {
        "Visible": addedText.getVisibilityInArea(areaName)
      }
    }
  }

  this.visibilityConfigurations["default"] = new Object();
  this.sideConfigurations["default"] = new Object();
  for (var objName in sceneHandler.getAddedObjects()){
    var obj = addedObjects[objName];
    this.visibilityConfigurations["default"][objName] = {
      "Visible": obj.getVisibilityInArea("default")
    };
    this.sideConfigurations["default"][objName] = {
      "Side": obj.getSideInArea("default")
    };
  }
  for (var objName in sceneHandler.getObjectGroups()){
    var obj = objectGroups[objName];
    this.visibilityConfigurations["default"][objName] = {
      "Visible": obj.getVisibilityInArea("default")
    };
    this.sideConfigurations["default"][objName] = {
      "Side": obj.getSideInArea("default")
    };
  }
  for (var textName in sceneHandler.getAddedTexts()){
    var addedText = addedTexts[textName];
    if (addedText.is2D){
      continue;
    }
    this.visibilityConfigurations["default"][textName] = {
      "Visible": addedText.getVisibilityInArea("default")
    }
  }
}

AreaConfigurationsHandler.prototype.show = function(){
  this.generateConfigurations();
  guiHandler.datGuiAreaConfigurations = new dat.GUI({hideable: false});
  areaConfigurationsVisible = true;

  for (var areaName in sceneHandler.getAreas()){
    var areaFolder = guiHandler.datGuiAreaConfigurations.addFolder(areaName);
    this.addSubFolder(areaName, areaFolder);
  }

  var defaultFolder = guiHandler.datGuiAreaConfigurations.addFolder("default");
  this.addSubFolder("default", defaultFolder);

  guiHandler.datGuiAreaConfigurations.add({
    "Done": function(){
      guiHandler.hide(guiHandler.guiTypes.AREA);
      terminal.clear();
      terminal.printInfo(Text.GUI_CLOSED);
    }
  }, "Done");
}

AreaConfigurationsHandler.prototype.addSubFolder = function(areaName, folder){
  var areaConfigurationsHandlerContext = this;
  for (var objName in sceneHandler.getAddedObjects()){
    var obj = addedObjects[objName];
    if (obj.isChangeable || obj.isDynamicObject){
      continue;
    }
    var objFolder = folder.addFolder(objName);
    var visibilityController = objFolder.add(this.visibilityConfigurations[areaName][objName], "Visible");
    var sideController = objFolder.add(this.sideConfigurations[areaName][objName], "Side", this.sideAry);
    visibilityController.onChange(function(val){
      this.object.setVisibilityInArea(this.areaName, val);
      if (areaConfigurationsHandlerContext.currentArea){
        delete areaConfigurationsHandlerContext.currentArea;
      }
    }.bind({object: addedObjects[objName], areaName: areaName}));
    sideController.onChange(function(val){
      this.object.setSideInArea(this.areaName, val);
      if (areaConfigurationsHandlerContext.currentArea){
        delete areaConfigurationsHandlerContext.currentArea;
      }
    }.bind({object: addedObjects[objName], areaName: areaName}));
  }
  for (var objName in sceneHandler.getObjectGroups()){
    var obj = objectGroups[objName];
    if (obj.isChangeable || obj.isDynamicObject){
      continue;
    }
    var objFolder = folder.addFolder(objName);
    var visibilityController = objFolder.add(this.visibilityConfigurations[areaName][objName], "Visible");
    var sideController = objFolder.add(this.sideConfigurations[areaName][objName], "Side", this.sideAry);
    visibilityController.onChange(function(val){
      this.object.setVisibilityInArea(this.areaName, val);
      if (areaConfigurationsHandlerContext.currentArea){
        delete areaConfigurationsHandlerContext.currentArea;
      }
    }.bind({object: objectGroups[objName], areaName: areaName}));
    sideController.onChange(function(val){
      this.object.setSideInArea(this.areaName, val);
      if (areaConfigurationsHandlerContext.currentArea){
        delete areaConfigurationsHandlerContext.currentArea;
      }
    }.bind({object: objectGroups[objName], areaName: areaName}));
  }
  for (var textName in sceneHandler.getAddedTexts()){
    var addedText = addedTexts[textName];
    if (addedText.is2D){
      continue;
    }
    var textFolder = folder.addFolder(textName);
    var visibilityController = textFolder.add(this.visibilityConfigurations[areaName][textName], "Visible");
    visibilityController.onChange(function(val){
      this.addedText.setVisibilityInArea(this.areaName, val);
      if (areaConfigurationsHandlerContext.currentArea){
        delete areaConfigurationsHandlerContext.currentArea;
      }
    }.bind({addedText: addedText, areaName: areaName}));
  }
}

var RayCaster = function(){
  this.binHandler = new WorldBinHandler();
  this.origin = new THREE.Vector3();
  this.direction = new THREE.Vector3();
  this.oldPosition = new THREE.Vector3();
  this.updateBuffer = new Map();
  this.ready = false;
}

RayCaster.prototype.hide2D = function(object){
  objectPicker2D.hide(object);
}

RayCaster.prototype.show2D = function(object){
  objectPicker2D.show(object);
}

RayCaster.prototype.update2D = function(object, forceUpdate){
  objectPicker2D.update(object, forceUpdate);
}

RayCaster.prototype.refresh2D = function(){
  objectPicker2D.refresh();
}

RayCaster.prototype.onReady = function(){
  this.ready = true;
  if (this.onReadyCallback){
    this.onReadyCallback();
  }
  if (!IS_WORKER_CONTEXT){
    sceneHandler.onRaycasterReady();
  }
}

RayCaster.prototype.flush = function(){
  this.updateBuffer.forEach(this.issueUpdate);
  this.updateBuffer.clear();
}

RayCaster.prototype.refresh = function(){
  if (!projectLoaded){
    return;
  }
  this.ready = false;
  this.refresh2D();
  this.binHandler = new WorldBinHandler();
  for (var objName in this.getAddedObjects()){
    var addedObject = addedObjects[objName];
    if (mode == 1 && !addedObject.isIntersectable){
      continue;
    }
    if (mode == 0 && addedObject.hiddenInDesignMode){
      continue;
    }
    addedObject.mesh.updateMatrix();
    addedObject.mesh.updateMatrixWorld();
    if (!addedObject.boundingBoxes){
      addedObject.generateBoundingBoxes();
    }else{
      addedObject.updateBoundingBoxes();
    }
    this.binHandler.insert(addedObject.boundingBoxes[0], objName);
  }
  for (var objName in this.getObjectGroups()){
    var objectGroup = objectGroups[objName];
    if (mode == 1 && !objectGroup.isIntersectable){
      continue;
    }
    if (mode == 0 && objectGroup.hiddenInDesignMode){
      continue;
    }
    objectGroup.mesh.updateMatrix();
    objectGroup.mesh.updateMatrixWorld();
    if (!objectGroup.boundingBoxes){
      objectGroup.generateBoundingBoxes();
    }else{
      objectGroup.updateBoundingBoxes();
    }
    for (var i = 0; i<objectGroup.boundingBoxes.length; i++){
      this.binHandler.insert(objectGroup.boundingBoxes[i], objectGroup.boundingBoxes[i].roygbivObjectName, objName);
    }
  }
  if (mode == 0){
    for (var gsName in this.getGridSystems()){
      var gridSystem = gridSystems[gsName];
      this.binHandler.insert(gridSystem.boundingBox, gridSystem.name);
    }
    for (var txtName in this.getAddedTexts()){
      var addedText = addedTexts[txtName];
      if (mode == 0 && addedText.hiddenInDesignMode){
        continue;
      }
      if (!addedText.is2D){
        this.binHandler.insert(addedText.boundingBox, txtName);
      }
    }
  }else{
    for (var txtName in this.getAddedTexts()){
      var addedText = addedTexts[txtName];
      if (addedText.isClickable && !addedText.is2D){
        this.binHandler.insert(addedText.boundingBox, txtName);
      }
    }
  }
  this.onReady();
}

RayCaster.prototype.updateObject = function(obj, forceUpdate){
  if ((obj.isAddedText && obj.is2D) || (obj.isSprite && !obj.isBackgroundObject) || (obj.isContainer)){
    this.update2D(obj, forceUpdate);
    return;
  }
  if (forceUpdate){
    this.binHandler.updateObject(obj);
    return;
  }
  this.updateBuffer.set(obj.name, obj);
}

RayCaster.prototype.issueUpdate = function(obj){
  if (!(mode == 1 && (obj.isAddedObject || obj.isObjectGroup) && !obj.isIntersectable)){
    if (obj.isHidden){
      return;
    }
    rayCaster.binHandler.updateObject(obj);
  }
}

RayCaster.prototype.findIntersections = function(from, direction, intersectGridSystems, callbackFunction, clientX, clientY, skip2D){
  intersectionPoint = 0, intersectionObject = 0;
  if (!IS_WORKER_CONTEXT && !skip2D){
    objectPicker2D.find(clientX, clientY);
    if (intersectionPoint){
      callbackFunction();
      return;
    }
  }
  this.origin.copy(from);
  this.direction.copy(direction);
  this.origin.addScaledVector(this.direction, RAYCASTER_STEP_AMOUNT);
  this.oldPosition.copy(this.origin);
  var iterate = true;
  if (!isDeployment && mode == 0){
    if (keyboardBuffer["Alt"]){
      intersectGridSystems = false;
    }
  }
  while (iterate){
    REUSABLE_LINE.set(this.oldPosition, this.origin);
    var results = this.binHandler.query(this.origin);
    for (var objName in results){
      var result = results[objName];
      if (result == 5){
        var obj = addedObjects[objName];
        if (obj){
          if (!(mode == 0 && keyboardBuffer["Shift"])){
            intersectionPoint = obj.intersectsLine(REUSABLE_LINE);
            if (intersectionPoint){
              intersectionObject = objName;
              callbackFunction(intersectionPoint.x, intersectionPoint.y, intersectionPoint.z, intersectionObject);
              return;
            }
          }
        }
      }else if (result == 10){
        var gs = gridSystems[objName];
        if (gs && intersectGridSystems){
          intersectionPoint = gs.intersectsLine(REUSABLE_LINE);
          if (intersectionPoint){
            intersectionObject = objName;
            callbackFunction(intersectionPoint.x, intersectionPoint.y, intersectionPoint.z, intersectionObject);
            return;
          }
        }
      }else if (result == 20){
        var addedText = addedTexts[objName];
        if (addedText && addedText.plane){
          intersectionPoint = addedText.intersectsLine(REUSABLE_LINE);
          if (intersectionPoint){
            intersectionObject = objName;
            callbackFunction(intersectionPoint.x, intersectionPoint.y, intersectionPoint.z, intersectionObject);
            return;
          }
        }
      }else{
        if (!(mode == 0 && keyboardBuffer["Shift"])){
          var parent = objectGroups[objName];
          if (parent){
            for (var childName in result){
              var obj = parent.group[childName];
              if (obj){
                if (!(mode == 0 && keyboardBuffer["Shift"])){
                  intersectionPoint = obj.intersectsLine(REUSABLE_LINE);
                  if (intersectionPoint){
                    intersectionObject = objName;
                    callbackFunction(intersectionPoint.x, intersectionPoint.y, intersectionPoint.z, intersectionObject);
                    return;
                  }
                }
              }
            }
          }
        }
      }
    }
    this.oldPosition.copy(this.origin);
    this.origin.addScaledVector(this.direction, RAYCASTER_STEP_AMOUNT);
    iterate = LIMIT_BOUNDING_BOX.containsPoint(this.origin);
    if (this.oldPosition.x == this.origin.x && this.oldPosition.y == this.origin.y && this.oldPosition.z == this.origin.z){
      iterate = false;
    }
  }
  callbackFunction(0, 0, 0, null);
}

