var LightweightState = function(){
  this.isLightweightState = true;
  // MODE
  this.mode = mode;
  // OCTREE LIMITS
  this.octreeLimit = {
    minX: LIMIT_BOUNDING_BOX.min.x, minY: LIMIT_BOUNDING_BOX.min.y, minZ: LIMIT_BOUNDING_BOX.min.z,
    maxX: LIMIT_BOUNDING_BOX.max.x, maxY: LIMIT_BOUNDING_BOX.max.y, maxZ: LIMIT_BOUNDING_BOX.max.z
  }
  // BIN SIZE
  this.binSize = BIN_SIZE;
  // RAYCASTER STEP AMOUNT
  this.raycasterStepAmount = RAYCASTER_STEP_AMOUNT;
  // VIEWPORT
  var vp = renderer.getCurrentViewport();
  this.viewport = {x: vp.x, y: vp.y, z: vp.z, w: vp.w}
  this.screenResolution = screenResolution;
  // PHYSICS DATA
  this.quatNormalizeSkip = quatNormalizeSkip;
  this.quatNormalizeFast = quatNormalizeFast;
  this.contactEquationStiffness = contactEquationStiffness;
  this.contactEquationRelaxation = contactEquationRelaxation;
  this.friction = friction;
  this.physicsIterations = physicsIterations;
  this.physicsTolerance = physicsTolerance;
  this.gravityY = gravityY;
  // CAMERA
  this.camera = {
    position: {x: camera.position.x, y: camera.position.y, z: camera.position.z},
    quaternion: {x: camera.quaternion.x, y: camera.quaternion.y, z: camera.quaternion.z, w: camera.quaternion.w},
    aspect: camera.aspect,
    fov: camera.fov
  }
  // GRID SYSTEMS
  this.gridSystems = new Object();
  for (var gsName in sceneHandler.getGridSystems()){
    this.gridSystems[gsName] = gridSystems[gsName].exportLightweight();
  }
  // ADDED OBJECTS
  this.addedObjects = new Object();
  for (var objName in sceneHandler.getAddedObjects()){
    this.addedObjects[objName] = addedObjects[objName].exportLightweight();
  }
  // OBJECT GROUPS
  this.childAddedObjects = new Object();
  this.objectGroups = new Object();
  for (var objName in sceneHandler.getObjectGroups()){
    this.objectGroups[objName] = objectGroups[objName].exportLightweight();
    for (var childName in objectGroups[objName].group){
      this.childAddedObjects[childName] = objectGroups[objName].group[childName].exportLightweight();
    }
  }
  // 3D ADDED TEXTS
  this.addedTexts3D = new Object();
  for (var textName in sceneHandler.getAddedTexts()){
    if (!addedTexts[textName].is2D){
      this.addedTexts3D[textName] = addedTexts[textName].exportLightweight();
    }
  }
  // 2D ADDED TEXTS
  this.addedTexts2D = new Object();
  for (var textName in sceneHandler.getAddedTexts2D()){
    if (addedTexts[textName].is2D){
      this.addedTexts2D[textName] = addedTexts[textName].exportLightweight();
    }
  }
  // SPRITES
  this.sprites = new Object();
  for (var spriteName in sceneHandler.getSprites()){
    this.sprites[spriteName] = sprites[spriteName].exportLightweight();
  }
  // CONTAINERS
  this.containers = new Object();
  for (var containerName in sceneHandler.getContainers()){
    this.containers[containerName] = containers[containerName].exportLightweight();
  }
  // VIRTUAL KEYBOARDS
  this.virtualKeyboards = new Object();
  for (var virtualKeyboardName in sceneHandler.getVirtualKeyboards()){
    this.virtualKeyboards[virtualKeyboardName] = virtualKeyboards[virtualKeyboardName].exportLightweight();
  }
  // PARTICLE SYSTEMS
  this.particleSystems = new Object();
  for (var psName in particleSystemPool){
    var ps = particleSystemPool[psName];
    if (ps.registeredSceneName != sceneHandler.getActiveSceneName()){
      continue;
    }
    if (ps.shouldSendToWorker()){
      this.particleSystems[psName] = ps.creationConfigurations;
      var particles = new Object();
      ps.particlesWithCollisionCallbacks.forEach(function(particle, uuid){
        particles[uuid] = particle.creationConfigurations;
      });
      this.particleSystems[psName].particles = particles;
    }
  }
  // MASSES
  this.masses = new Object();
  var masses = sceneHandler.getMasses();
  for (var massName in masses){
    var mass = masses[massName];
    this.masses[massName] = mass.export();
  }
}

var PointerLockEventHandler = function(){
  if (isMobile){
    return;
  }
  pointerLockSupported = 'pointerLockElement' in document || 'mozPointerLockElement' in document || 'webkitPointerLockElement' in document;
  canvas.requestPointerLock = canvas.requestPointerLock || canvas.mozRequestPointerLock || canvas.webkitRequestPointerLock;
  var pointerLockChangeFunction = 0;
  if ("onpointerlockchange" in document){
    pointerLockChangeFunction = "pointerlockchange";
  }else if ("onmozpointerlockchange" in document){
    pointerLockChangeFunction = "mozpointerlockchange";
  }else if ("onwebkitpointerlockchange" in document){
    pointerLockChangeFunction = "webkitpointerlockchange";
  }
  if (pointerLockChangeFunction){
    document.addEventListener(pointerLockChangeFunction, this.onPointerLock);
  }
  this.isPointerLocked = false;
}

PointerLockEventHandler.prototype.onPointerLock = function(event){
  var isPointerLocked = document.pointerLockElement == canvas || document.mozPointerLockElement == canvas || document.webkitPointerLockElement == canvas;
  pointerLockEventHandler.isPointerLocked = isPointerLocked;
  if (mode == 1 && screenPointerLockChangedCallbackFunction){
    if (isPointerLocked){
      screenPointerLockChangedCallbackFunction(true);
      pointerLockRequested = false;
    }else{
      screenPointerLockChangedCallbackFunction(false);
    }
  }
  if (activeControl.onPointerLockChange){
    activeControl.onPointerLockChange(isPointerLocked);
  }
}

var TouchEventHandler = function(){
  if (!isMobile){
    return;
  }
  canvas.addEventListener('touchstart', this.onTouchStart, false);
  canvas.addEventListener('touchmove', this.onTouchMove, false);
  canvas.addEventListener('touchcancel', this.onTouchEnd, false);
  canvas.addEventListener('touchend', this.onTouchEnd, false);
  this.distance = 0;
  this.isZooming = false;
  this.isSwiping = false;
  this.isTapping = false;
  this.lastSwipeCoordinates = {x: 0, y: 0, isInitiated: false};
  this.tapStartTime = 0;
  this.isThereFingerTouched = false;
  this.currentTouchCount = 0;
  this.tapThreshold = 310;
  this.touchTrack = new Map();
}

TouchEventHandler.prototype.onTouchStart = function(event){
  event.preventDefault();
  activeControl.onTouchStart(event);
  for (var i = 0; i<event.changedTouches.length; i++){
    var curTouch = event.changedTouches[i];
    touchEventHandler.touchTrack.set(curTouch.identifier, curTouch);
  }
  touchEventHandler.isZooming = false;
  touchEventHandler.isSwiping = false;
  touchEventHandler.isTapping = false;
  touchEventHandler.isThereFingerTouched = true;
  touchEventHandler.currentTouchCount = event.targetTouches.length;
  if (event.targetTouches.length == 1){
    touchEventHandler.isSwiping = true;
    touchEventHandler.isTapping = true;
  }
  if (event.targetTouches.length == 2){
    touchEventHandler.isZooming = true;
    touchEventHandler.isTapping = true;
  }
  if (!touchEventHandler.isZooming){
    touchEventHandler.distance = 0;
  }
  if (!touchEventHandler.isSwiping){
    touchEventHandler.lastSwipeCoordinates.isInitiated = false;
  }
  if (touchEventHandler.isTapping){
    touchEventHandler.tapStartTime = performance.now();
  }else{
    touchEventHandler.tapStartTime = 0;
  }

  if (event.changedTouches.length == 1 && mode == 1 && sceneHandler.hasDraggableSprite()){
    var touch = event.changedTouches[0];
    var rect = boundingClientRect;
    var coordX = ((touch.clientX - rect.left) / rect.width) * 2 - 1;
    var coordY = - ((touch.clientY - rect.top) / rect.height) * 2 + 1;
    REUSABLE_VECTOR.setFromMatrixPosition(camera.matrixWorld);
    REUSABLE_VECTOR_2.set(coordX, coordY, 0.5).unproject(camera).sub(REUSABLE_VECTOR).normalize();
    rayCaster.findIntersections(REUSABLE_VECTOR, REUSABLE_VECTOR_2, (mode == 0), onRaycasterMouseDownIntersection, touch.clientX, touch.clientY);
  }
}

TouchEventHandler.prototype.onTouchMove = function(event){
  event.preventDefault();
  event.stopPropagation();
  activeControl.onTouchMove(event);
  for (var i = 0; i<event.changedTouches.length; i++){
    var curTouch = event.changedTouches[i];
    touchEventHandler.touchTrack.set(curTouch.identifier, curTouch);
  }
  if (touchEventHandler.isZooming){
    if (event.changedTouches.length == 2){
      var t1 = event.changedTouches[0];
      var t2 = event.changedTouches[1];
      var xs = (t1.pageX - t2.pageX) * (t1.pageX - t2.pageX);
      var ys = (t1.pageY - t2.pageY) * (t1.pageY - t2.pageY);
      var dist = Math.sqrt(xs + ys);
      if (touchEventHandler.distance){
        touchEventHandler.onPinch(dist - touchEventHandler.distance);
      }
      touchEventHandler.distance = dist;
    }
  }
  if (touchEventHandler.isSwiping){
    if (event.changedTouches.length == 1){
      var t1 = event.changedTouches[0];
      if (touchEventHandler.lastSwipeCoordinates.isInitiated){
        var diffX = t1.clientX - touchEventHandler.lastSwipeCoordinates.x;
        var diffY = t1.clientY - touchEventHandler.lastSwipeCoordinates.y;
        if (mode ==1 && dragCandidate){
          draggingSprite = dragCandidate;
          dragCandidate = false;
          draggingSprite.onDragStarted(diffX, diffY);
        }
        activeControl.onSwipe(t1.clientX, t1.clientY, diffX, diffY);
        if (draggingSprite){
          draggingSprite.onDrag(diffX * 1.7, diffY * 1.7);
        }
      }
      touchEventHandler.lastSwipeCoordinates.x = t1.clientX;
      touchEventHandler.lastSwipeCoordinates.y = t1.clientY;
      touchEventHandler.lastSwipeCoordinates.isInitiated = true;
    }
  }
}

TouchEventHandler.prototype.onPinch = function(diff){
  if (mode == 1 && screenPinchCallbackFunction){
    screenPinchCallbackFunction(diff);
  }
  activeControl.onPinch(diff);
}

TouchEventHandler.prototype.onTap = function(touch){
  mouseEventHandler.onClick(touch, true);
  activeControl.onTap(touch);
  if (mode == 0 && !isDeployment){
    virtualKeyboardCreatorGUIHandler.onClick(touch.clientX, touch.clientY);
  }
}

TouchEventHandler.prototype.onTouchEnd = function(event){
  event.preventDefault();
  activeControl.onTouchEnd(event);
  for (var i = 0; i<event.changedTouches.length; i++){
    touchEventHandler.touchTrack.delete(event.changedTouches[i].identifier);
  }
  if (touchEventHandler.isTapping){
    if(performance.now() - touchEventHandler.tapStartTime < touchEventHandler.tapThreshold){
      touchEventHandler.onTap(event.changedTouches[0]);
    }
  }
  touchEventHandler.isZooming = false;
  touchEventHandler.isSwiping = false;
  touchEventHandler.isTapping = false;
  touchEventHandler.distance = 0;
  touchEventHandler.lastSwipeCoordinates.isInitiated = false;
  touchEventHandler.tapStartTime = 0;
  touchEventHandler.currentTouchCount = event.targetTouches.length;
  if (touchEventHandler.currentTouchCount == 0){
    touchEventHandler.isThereFingerTouched = false;
  }
  if (mode == 1 && event.changedTouches.length == 1 && draggingSprite){
    draggingSprite.onDragStopped();
  }
}

