MouseEventHandler.prototype.onDrag = function(x, y, movementX, movementY){
  if (mode == 1 && screenDragCallbackFunction){
    screenDragCallbackFunction(x, y, movementX, movementY);
  }
  activeControl.onDrag(x, y, movementX, movementY);
}

MouseEventHandler.prototype.onMouseMove = function(event){
  inactiveCounter = 0;
  var rect = boundingClientRect;
  mouseEventHandler.clientX = event.clientX;
  mouseEventHandler.clientY = event.clientY;
  mouseEventHandler.coordX = ((event.clientX - rect.left) / rect.width) * 2 - 1;
  mouseEventHandler.coordY = - ((event.clientY - rect.top) / rect.height) * 2 + 1;
  mouseEventHandler.movementX = event.movementX || event.mozMovementX || event.webkitMovementX || 0;
  mouseEventHandler.movementY = event.movementY || event.mozMovementY || event.webkitMovementY || 0;
  if (mode == 1 && screenMouseMoveCallbackFunction){
    screenMouseMoveCallbackFunction(mouseEventHandler.coordX, mouseEventHandler.coordY, mouseEventHandler.movementX, mouseEventHandler.movementY);
  }
  var diffX = mouseEventHandler.clientX - mouseEventHandler.oldClientX;
  var diffY = mouseEventHandler.clientY - mouseEventHandler.oldClientY;
  if (mode == 1 && dragCandidate){
    draggingSprite = dragCandidate;
    dragCandidate = false;
    draggingSprite.onDragStarted(diffX, diffY);
  }
  if (mode == 1 && draggingSprite){
    draggingSprite.onDrag(diffX, diffY);
  }
  mouseEventHandler.oldClientX = mouseEventHandler.clientX;
  mouseEventHandler.oldClientY = mouseEventHandler.clientY;
  mouseEventHandler.eventBuffer.mouseMove.needsFlush = true;
  mouseEventHandler.eventBuffer.mouseMove.event = event;
  if (isMouseDown && !isMobile){
    mouseEventHandler.onDrag(mouseEventHandler.clientX, mouseEventHandler.clientY, mouseEventHandler.movementX, mouseEventHandler.movementY);
  }
}

MouseEventHandler.prototype.onMouseUp = function(event){
  inactiveCounter = 0;
  if (mode == 1 && screenMouseUpCallbackFunction){
    var rect = boundingClientRect;
    var coordX = ((event.clientX - rect.left) / rect.width) * 2 - 1;
    var coordY = - ((event.clientY - rect.top) / rect.height) * 2 + 1;
    screenMouseUpCallbackFunction(coordX, coordY);
  }
  if (mode == 1 && draggingSprite){
    draggingSprite.onDragStopped();
  }
  dragCandidate = false;
  isMouseDown = false;
  mouseEventHandler.eventBuffer.mouseUp.event = event;
  mouseEventHandler.eventBuffer.mouseUp.needsFlush = true;
}

MouseEventHandler.prototype.onMouseDown = function(event){
  if (!isMobile){
    mouseEventHandler.lastMouseDownTime = performance.now();
  }
  inactiveCounter = 0;
  var rect = boundingClientRect;
  var coordX = ((event.clientX - rect.left) / rect.width) * 2 - 1;
  var coordY = - ((event.clientY - rect.top) / rect.height) * 2 + 1;
  mouseEventHandler.oldClientX = event.clientX;
  mouseEventHandler.oldClientY = event.clientY;
  if (mode == 1 && screenMouseDownCallbackFunction){
    screenMouseDownCallbackFunction(coordX, coordY);
  }
  if (mode == 1 && sceneHandler.hasDraggableSprite()){
    REUSABLE_VECTOR.setFromMatrixPosition(camera.matrixWorld);
    REUSABLE_VECTOR_2.set(coordX, coordY, 0.5).unproject(camera).sub(REUSABLE_VECTOR).normalize();
    rayCaster.findIntersections(REUSABLE_VECTOR, REUSABLE_VECTOR_2, (mode == 0), onRaycasterMouseDownIntersection, event.clientX, event.clientY);
  }
  isMouseDown = true;
  mouseEventHandler.eventBuffer.mouseDown.event = event;
  mouseEventHandler.eventBuffer.mouseDown.needsFlush = true;
}

MouseEventHandler.prototype.onClick = function(event, fromTap){
  inactiveCounter = 0;
  cliFocused = false;
  omGUIFocused = false;
  tmGUIFocused = false;
  smGUIFocused = false;
  cmGUIFocused = false;
  acGUIFocused = false;
  lGUIFocused = false;
  vkGUIFocused = false;
  if (windowLoaded){
    if (mode == 0){
      if (!isDeployment && guiHandler.isOneOfBlockingGUIActive()){
        return;
      }
      if (shadowBaker.isBakingShadow){
        return;
      }
    }
    var rect = renderer.getCurrentViewport();
    var rectX = rect.x, rectY = rect.y, rectZ = rect.z, rectW = rect.w;
    if (screenResolution != 1){
      rectX = rectX / screenResolution;
      rectY = rectY / screenResolution;
      rectZ = rectZ / screenResolution;
      rectW = rectW / screenResolution;
    }
    var coordX = ((event.clientX - rectX) / rectZ) * 2 - 1;
    var coordY = - ((event.clientY - rectY) / rectW) * 2 + 1;
    if (mode == 1 && screenClickCallbackFunction){
      screenClickCallbackFunction(coordX, coordY);
    }
    if (mode == 1 && pointerLockSupported && pointerLockRequested){
      canvas.requestPointerLock();
    }
    if (mode == 1 && fullScreenRequested){
      if (canvas.requestFullscreen){
        canvas.requestFullscreen();
      } else if (canvas.mozRequestFullScreen){
        canvas.mozRequestFullScreen();
      } else if (canvas.webkitRequestFullscreen){
        canvas.webkitRequestFullscreen();
      } else if (canvas.msRequestFullscreen){
        canvas.msRequestFullscreen();
      }
      fullScreenRequested = false;
    }
    if (mode == 1 && isPaused){
      return;
    }
    if (event.clientX < rectX || event.clientX > rectX + rectZ || event.clientY < rectY || event.clientY > rectY + rectW){
      return;
    }
    if (!fromTap){
      activeControl.onClick(event);
    }
    if (mode == 1 && objectsWithOnClickListeners.size == 0 && !activeVirtualKeyboard){
      return;
    }
    if (!isMobile && mouseEventHandler.lastMouseDownTime){
      if (performance.now() - mouseEventHandler.lastMouseDownTime >= 500){
        return;
      }
    }
    if (mode == 0){
      if (!isDeployment && guiHandler.isOneOfBlockingGUIActive()){
        return;
      }
    }
    REUSABLE_VECTOR.setFromMatrixPosition(camera.matrixWorld);
    REUSABLE_VECTOR_2.set(coordX, coordY, 0.5).unproject(camera).sub(REUSABLE_VECTOR).normalize();
    rayCaster.findIntersections(REUSABLE_VECTOR, REUSABLE_VECTOR_2, (mode == 0), onRaycasterIntersection, event.clientX, event.clientY);
  }
}

var ResizeEventHandler = function(){
  window.addEventListener('resize', this.onResize);
}

ResizeEventHandler.prototype.onResize = function(){
  var oldOrientation = isOrientationLandscape;
  isOrientationLandscape = (window.innerWidth > window.innerHeight);
  if (!(renderer)){
    return;
  }
  if (oldOrientation != isOrientationLandscape && isMobile && screenOrientationChangeCallbackFunction){
    screenOrientationChangeCallbackFunction(isOrientationLandscape);
  }
  renderer.setSize(window.innerWidth, window.innerHeight);
  camera.oldAspect = camera.aspect;
  camera.aspect = window.innerWidth / window.innerHeight;
  camera.updateProjectionMatrix();
  boundingClientRect = renderer.getBoundingClientRect();
  if (isDeployment){
    canvas.oldWidth = (canvas.width / screenResolution) + 'px';
    if (!isDeployment && terminal.isMadeVisible){
      ROYGBIV.terminal(false);
      ROYGBIV.terminal(true);
      if (!terminal.terminalPromptEnabled){
        ROYGBIV.terminalPrompt(false);
      }
    }
  }
  if (mode == 1){
    handleViewport();
  }else if (!isDeployment){
    if (fpsWeaponGUIHandler.fpsWeaponAlignmentConfigurationObject){
      fpsWeaponGUIHandler.fpsWeaponAlignmentConfigurationObject.onFPSWeaponAlignmentUpdate();
    }
    if (virtualKeyboardCreatorGUIHandler.virtualKeyboard){
      virtualKeyboardCreatorGUIHandler.virtualKeyboard.handleResize();
    }
  }
  if (particleSystemRefHeight){
    GLOBAL_PS_REF_HEIGHT_UNIFORM.value = ((renderer.getCurrentViewport().w / screenResolution) / particleSystemRefHeight);
  }
  if (mode == 0){
    for (var areaName in areas){
      if (areas[areaName].text){
        areas[areaName].text.handleResize();
      }
    }
    for (var pointName in markedPoints){
      if (markedPoints[pointName].text){
        markedPoints[pointName].text.handleResize();
      }
    }
    for (var gridName in gridSelections){
      if (gridSelections[gridName].texts){
        for (var i = 0; i<gridSelections[gridName].texts.length; i++){
          gridSelections[gridName].texts[i].handleResize();
        }
      }
    }
  }else{
    for (var crosshairName in crosshairs){
      crosshairs[crosshairName].handleResize();
    }
  }
  for (var textName in addedTexts){
    addedTexts[textName].handleResize();
  }
  for (var spriteName in sprites){
    sprites[spriteName].handleResize();
  }
  for (var containerName in containers){
    containers[containerName].handleResize();
  }
  for (var virtualKeyboardName in virtualKeyboards){
    virtualKeyboards[virtualKeyboardName].handleResize();
  }
  renderer.setPixelRatio(screenResolution);
  resizeEventHandler.refresh2DNeeded = true;
  activeControl.onResize();
}

var KeyboardEventHandler = function(){
  if (isMobile){
    return;
  }
  window.addEventListener('keydown', this.onKeyDown);
  window.addEventListener('keyup', this.onKeyUp);

  this.CAPSLOCK = "CapsLock";
  this.SHIFT = "Shift";
}

KeyboardEventHandler.prototype.onKeyUp = function(event){
  inactiveCounter = 0;
  if (!windowLoaded){
    return;
  }
  keyboardEventHandler.isCapsOn = event.getModifierState && event.getModifierState(keyboardEventHandler.CAPSLOCK);
  if (cliFocused || omGUIFocused || tmGUIFocused || smGUIFocused || cmGUIFocused || acGUIFocused || lGUIFocused || vkGUIFocused){
    return;
  }
  if (keyCodeToChar[event.keyCode] == keyboardEventHandler.SHIFT){
    for (var key in shiftCombinationKeys){
      keyboardBuffer[shiftCombinationKeys[key]] = false;
    }
  }
  if (keyCodeToChar[event.keyCode]){
    keyboardBuffer[keyCodeToChar[event.keyCode]] = false;
    if (mode == 0 && keyCodeToChar[event.keyCode] == "."){
      for (var gridName in gridSelections){
        gridSelections[gridName].removeCornerHelpers();
      }
    }
    if (shiftCombinationKeys[event.keyCode]){
      keyboardBuffer[shiftCombinationKeys[event.keyCode]] = false;
    }
    if (mode == 1 && !isPaused && screenKeyupCallbackFunction){
      screenKeyupCallbackFunction(keyCodeToChar[event.keyCode]);
    }
  }
  activeControl.onKeyUp(event);
  if (mode == 0 && !isDeployment){
    if (guiHandler.isOneOfBlockingGUIActive()){
      return;
    }
  }
  if (mode == 1 && isPaused){
    return;
  }
  switch(event.keyCode){
    case 190: //PERIOD
      for (var gridName in gridSelections){
        var grid = gridSelections[gridName];
        if (grid.divs){
          for (var i = 0; i<grid.divs.length; i++){
            grid.divs[i].style.visibility = "hidden";
          }
        }
      }
    break;
    case 16: //SHIFT
      if (mode == 0 && !isDeployment){
        keyboardEventHandler.deactivateGridSelectionMode();
      }
    break;
    case 18: // ALT
      if (mode == 0 && !isDeployment){
        keyboardEventHandler.deactivateObjectSelectionMode();
      }
    break;
  }
}

KeyboardEventHandler.prototype.onKeyDown = function(event){
  inactiveCounter = 0;
  if (!windowLoaded){
    return;
  }
  keyboardEventHandler.isCapsOn = event.getModifierState && event.getModifierState(keyboardEventHandler.CAPSLOCK);
  if (cliFocused || omGUIFocused || tmGUIFocused || smGUIFocused || cmGUIFocused || acGUIFocused || lGUIFocused || vkGUIFocused){
    return;
  }
  var foundKey;
  if (keyboardBuffer[keyboardEventHandler.SHIFT]){
    foundKey = shiftCombinationKeys[event.keyCode];
    if (foundKey){
      keyboardBuffer[foundKey] = true;
      found = true;
    }
  }
  if (keyCodeToChar[event.keyCode] && !foundKey){
    if (keyboardBuffer[keyCodeToChar[event.keyCode]]){
      return;
    }
    foundKey = keyCodeToChar[event.keyCode];
    keyboardBuffer[foundKey] = true;
  }
  if (mode == 1 && screenKeydownCallbackFunction && !isPaused && foundKey){
    screenKeydownCallbackFunction(foundKey);
  }
  activeControl.onKeyDown(event);
  if (mode == 0 && isDeployment){
    if (guiHandler.isOneOfBlockingGUIActive()){
      return;
    }
  }
  if (mode == 0 && keyboardBuffer["."]){
    for (var gridName in gridSelections){
      gridSelections[gridName].renderCornerHelpers();
    }
  }
  if (mode == 1 && isPaused){
    return;
  }
  switch(event.keyCode){
    case 16: //SHIFT
      if (mode == 0 && !isDeployment){
        keyboardEventHandler.activateGridSelectionMode();
      }
    break;
    case 18: // ALT
      if (mode == 0 && !isDeployment){
        keyboardEventHandler.activateObjectSelectionMode();
      }
    break;
    case 8: //BACKSPACE
      if (mode == 1 || isDeployment){
        return;
      }
      //FIREFOX GO BACK FIX
      if (selectionHandler.getSelectedObject() && !cliFocused){
        event.preventDefault();
      }
      var currentSelection = selectionHandler.getSelectedObject();
      if (currentSelection.isAddedObject || currentSelection.isObjectGroup){
        terminal.clear();
        parseCommand("destroyObject " + currentSelection.name);
      }else if (currentSelection.isAddedText){
        terminal.clear();
        parseCommand("destroyText " + currentSelection.name);
      }else if (currentSelection.isSprite){
        terminal.clear();
        parseCommand("destroySprite " + currentSelection.name);
      }else if (currentSelection.isContainer){
        parseCommand("destroyContainer " + currentSelection.name);
      }
      guiHandler.afterObjectSelection();
    break;
    case 27: //ESC
    if (mode == 0 && !isDeployment){
      selectionHandler.resetCurrentSelection();
      terminal.clear();
      terminal.printInfo(Text.SELECTION_RESET);
    }
    break;
  }
}

KeyboardEventHandler.prototype.activateGridSelectionMode = function(){
  selectionHandler.resetCurrentSelection();
  for (var objName in sceneHandler.getAddedObjects()){
    if (addedObjects[objName].hiddenInDesignMode){
      continue;
    }
    addedObjects[objName].mesh.visible = false;
  }
  for (var objName in sceneHandler.getObjectGroups()){
    if (objectGroups[objName].hiddenInDesignMode){
      continue;
    }
    objectGroups[objName].mesh.visible = false;
  }
  for (var textName in sceneHandler.getAddedTexts()){
    if (addedTexts[textName].hiddenInDesignMode){
      continue;
    }
    addedTexts[textName].hide();
  }
  for (var spriteName in sceneHandler.getSprites()){
    if (sprites[spriteName].hiddenInDesignMode){
      continue;
    }
    sprites[spriteName].mesh.visible = false;
  }
  for (var containerName in sceneHandler.getContainers()){
    if (containers[containerName].hiddenInDesignMode){
      continue;
    }
    containers[containerName].hideVisually();
  }
  for (var virtualKeyboardName in sceneHandler.getVirtualKeyboards()){
    if (virtualKeyboards[virtualKeyboardName].hiddenInDesignMode){
      continue;
    }
    virtualKeyboards[virtualKeyboardName].onShiftPress(true);
  }
  raycasterFactory.onShiftPress(true);
}

KeyboardEventHandler.prototype.deactivateGridSelectionMode = function(){
  for (var objName in sceneHandler.getAddedObjects()){
    if (addedObjects[objName].hiddenInDesignMode){
      continue;
    }
    addedObjects[objName].mesh.visible = true;
  }
  for (var objName in sceneHandler.getObjectGroups()){
    if (objectGroups[objName].hiddenInDesignMode){
      continue;
    }
    objectGroups[objName].mesh.visible = true;
  }
  for (var textName in sceneHandler.getAddedTexts()){
    if (addedTexts[textName].hiddenInDesignMode){
      continue;
    }
    addedTexts[textName].show();
  }
  for (var spriteName in sceneHandler.getSprites()){
    if (sprites[spriteName].hiddenInDesignMode){
      continue;
    }
    sprites[spriteName].mesh.visible = true;
  }
  for (var containerName in sceneHandler.getContainers()){
    if (containers[containerName].hiddenInDesignMode){
      continue;
    }
    containers[containerName].showVisually();
  }
  for (var virtualKeyboardName in sceneHandler.getVirtualKeyboards()){
    if (virtualKeyboards[virtualKeyboardName].hiddenInDesignMode){
      continue;
    }
    virtualKeyboards[virtualKeyboardName].onShiftPress(false);
  }
  raycasterFactory.onShiftPress(false);
}

KeyboardEventHandler.prototype.activateObjectSelectionMode = function(){
  for (var gsName in sceneHandler.getGridSystems()){
    gridSystems[gsName].hide();
  }
  for (var gridName in gridSelections){
    gridSelections[gridName].hide();
  }
  raycasterFactory.onAltPress(true);
}

KeyboardEventHandler.prototype.deactivateObjectSelectionMode = function(){
  for (var gsName in sceneHandler.getGridSystems()){
    gridSystems[gsName].show();
  }
  for (var gridName in gridSelections){
    gridSelections[gridName].show();
  }
  raycasterFactory.onAltPress(false);
}

