AutoInstancedObject.prototype.updateObject = function(object){
  var index = this.orientationIndicesByObjectName.get(object.name);
  var alphaIndex = this.alphaIndicesByObjectName.get(object.name);
  var scaleIndex = this.scaleIndicesByObjectName.get(object.name);
  var orientationAry = this.mesh.material.uniforms.autoInstanceOrientationArray.value;
  var alphaAry = this.mesh.material.uniforms.autoInstanceAlphaArray.value;

  if (this.mesh.material.uniforms.autoInstanceScaleArray){
    var scaleAry = this.mesh.material.uniforms.autoInstanceScaleArray.value;
    scaleAry[scaleIndex].set(object.mesh.scale.x, object.mesh.scale.y, object.mesh.scale.z);
  }

  var position = object.mesh.position;
  var quaternion = object.mesh.quaternion;
  var alpha = object.getOpacity();
  orientationAry[index].set(orientationAry[index].x, position.x, position.y, position.z);
  orientationAry[index+1].set(quaternion.x, quaternion.y, quaternion.z, quaternion.w);
  alphaAry[alphaIndex] = alpha;
  if (alpha != 1){
    this.mesh.material.transparent = true;
  }
  if (object.hasEmissiveMap()){
    this.mesh.material.uniforms.autoInstanceEmissiveIntensityArray.value[alphaIndex] = object.getEmissiveIntensity();
    var emissiveColor = object.getEmissiveColor();
    this.mesh.material.uniforms.autoInstanceEmissiveColorArray.value[alphaIndex].set(emissiveColor.r, emissiveColor.g, emissiveColor.b);
  }
  if (object.hasDisplacementMap()){
    this.mesh.material.uniforms.autoInstanceDisplacementInfoArray.value[alphaIndex].x = object.getDisplacementScale();
    this.mesh.material.uniforms.autoInstanceDisplacementInfoArray.value[alphaIndex].y = object.getDisplacementBias();
    if (object.customDisplacementTextureMatrixInfo){
      this.mesh.material.uniforms.autoInstanceDisplacementTextureOffsetInfoArray.value[alphaIndex].x = object.customDisplacementTextureMatrixInfo.offsetX;
      this.mesh.material.uniforms.autoInstanceDisplacementTextureOffsetInfoArray.value[alphaIndex].y = object.customDisplacementTextureMatrixInfo.offsetY;
    }
  }
  if (object.hasTexture()){
    this.mesh.material.uniforms.autoInstanceTextureOffsetInfoArray.value[alphaIndex].x = object.getTextureOffsetX();
    this.mesh.material.uniforms.autoInstanceTextureOffsetInfoArray.value[alphaIndex].y = object.getTextureOffsetY();
  }
  if (object.hasAOMap()){
    this.mesh.material.uniforms.autoInstanceAOIntensityArray.value[alphaIndex] = object.getAOIntensity();
  }
}

AutoInstancedObject.prototype.hideObject = function(object){
  var index = this.orientationIndicesByObjectName.get(object.name);
  var orientationAry = this.mesh.material.uniforms.autoInstanceOrientationArray.value;
  orientationAry[index].x = -10;
}

AutoInstancedObject.prototype.showObject = function(object){
  var index = this.orientationIndicesByObjectName.get(object.name);
  var orientationAry = this.mesh.material.uniforms.autoInstanceOrientationArray.value;
  orientationAry[index].x = 10;
}

AutoInstancedObject.prototype.forceColor = function(object, r, g, b, a){
  var index = this.forcedColorIndicesByObjectName.get(object.name);
  var forcedColorAry = this.mesh.material.uniforms.autoInstanceForcedColorArray.value;
  forcedColorAry[index].set(a, r, g, b);
  if (a < 0){
    a = 0;
  }
  if (a > 1){
    a = 1;
  }
  if (a != 1 && !this.mesh.material.transparent){
    this.mesh.material.transparent = true;
  }
}

AutoInstancedObject.prototype.resetColor = function(object){
  var index = this.forcedColorIndicesByObjectName.get(object.name);
  var forcedColorAry = this.mesh.material.uniforms.autoInstanceForcedColorArray.value;
  forcedColorAry[index].set(-100, -100, -100, -100);
}

AutoInstancedObject.prototype.init = function(){
  this.pseudoObjectGroup.handleTextures();
  this.pseudoObjectGroup.mergeInstanced();
  var meshGenerator = new MeshGenerator(this.pseudoObjectGroup.geometry);
  var pseudoGraphicsGroup = new THREE.Object3D();
  pseudoGraphicsGroup.position.set(0, 0, 0);
  this.mesh = meshGenerator.generateInstancedMesh(pseudoGraphicsGroup, this.pseudoObjectGroup);

  this.mesh.geometry.removeAttribute("positionOffset");
  this.mesh.geometry.removeAttribute("quaternion");
  this.mesh.geometry.removeAttribute("alpha");
  this.mesh.frustumCulled = false;
  webglCallbackHandler.registerEngineObject(this);
  if (this.pseudoObjectGroup.hasAOMap()){
    macroHandler.injectMacro("HAS_AO", this.mesh.material, true, true);
  }
  if (this.pseudoObjectGroup.hasEmissiveMap()){
    macroHandler.injectMacro("HAS_EMISSIVE", this.mesh.material, true, true);
  }
  if (this.pseudoObjectGroup.hasDiffuseMap()){
    macroHandler.injectMacro("HAS_DIFFUSE", this.mesh.material, true, true);
  }
  if (this.pseudoObjectGroup.hasAlphaMap()){
    macroHandler.injectMacro("HAS_ALPHA", this.mesh.material, true, true);
  }
  if (this.pseudoObjectGroup.hasDisplacementMap() && VERTEX_SHADER_TEXTURE_FETCH_SUPPORTED){
    macroHandler.injectMacro("HAS_DISPLACEMENT", this.mesh.material, true, false);
  }
  if (this.pseudoObjectGroup.hasTexture){
    macroHandler.injectMacro("HAS_TEXTURE", this.mesh.material, true, true);
  }
  macroHandler.injectMacro("IS_AUTO_INSTANCED", this.mesh.material, true, true);
  var objCount = 0;
  var curIndex = 0;
  var forcedColorIndex = 0;
  this.orientationIndicesByObjectName = new Map();
  this.alphaIndicesByObjectName = new Map();
  this.forcedColorIndicesByObjectName = new Map();
  this.scaleIndicesByObjectName = new Map();
  var orientationIndices = [];
  var alphaIndices = [];
  var scaleIndices = [];
  var scaleAry = [];
  var orientationAry = [];
  var alphaAry = [];
  var forcedColorAry = [];
  var forcedColorIndices = [];
  var emissiveIntensityAry = [];
  var emissiveColorAry = [];
  var displacementInfoAry = [];
  var aoIntensityAry = [];
  var textureOffsetInfoAry = [];
  var displacementTextureOffsetInfoAry = [];
  var affectedByLightAry = [];
  var hasColorizableMember = false;
  for (var objName in this.objects){
    var obj = this.objects[objName];
    if (obj.affectedByLight){
      this.affectedByLight = true;
    }
    this.orientationIndicesByObjectName.set(objName, curIndex);
    this.alphaIndicesByObjectName.set(objName, objCount);
    this.scaleIndicesByObjectName.set(objName, objCount);
    orientationIndices.push(curIndex);
    alphaIndices.push(objCount);
    scaleIndices.push(objCount);
    affectedByLightAry.push(obj.affectedByLight? 10: -10);
    curIndex += 2;
    objCount ++;
    orientationAry.push(new THREE.Vector4(10, obj.mesh.position.x, obj.mesh.position.y, obj.mesh.position.z));
    orientationAry.push(new THREE.Vector4(obj.mesh.quaternion.x, obj.mesh.quaternion.y, obj.mesh.quaternion.z, obj.mesh.quaternion.w));
    alphaAry.push(obj.getOpacity());
    scaleAry.push(obj.mesh.scale.clone());
    obj.autoInstancedParent = this;
    if (obj.isColorizable){
      hasColorizableMember = true;
    }
    forcedColorAry.push(new THREE.Vector4(-100, -100, -100, -100));
    forcedColorIndices.push(forcedColorIndex);
    this.forcedColorIndicesByObjectName.set(objName, forcedColorIndex);
    forcedColorIndex ++;
    if (obj.hasEmissiveMap()){
      emissiveIntensityAry.push(obj.getEmissiveIntensity());
      emissiveColorAry.push(new THREE.Vector3(obj.getEmissiveColor().r, obj.getEmissiveColor().g, obj.getEmissiveColor().b));
    }else{
      emissiveIntensityAry.push(0);
      emissiveColorAry.push(new THREE.Vector3(WHITE_COLOR.r, WHITE_COLOR.g, WHITE_COLOR.b));
    }
    if (obj.hasDisplacementMap()){
      displacementInfoAry.push(new THREE.Vector2(obj.getDisplacementScale(), obj.getDisplacementBias()));
      if (obj.customDisplacementTextureMatrixInfo){
        displacementTextureOffsetInfoAry.push(new THREE.Vector2(obj.customDisplacementTextureMatrixInfo.offsetX, obj.customDisplacementTextureMatrixInfo.offsetY));
      }else{
        displacementTextureOffsetInfoAry.push(new THREE.Vector2(0, 0));
      }
    }else{
      displacementInfoAry.push(new THREE.Vector2(0, 0));
      displacementTextureOffsetInfoAry.push(new THREE.Vector2(0, 0));
    }
    if (obj.hasTexture()){
      textureOffsetInfoAry.push(new THREE.Vector2(obj.getTextureOffsetX(), obj.getTextureOffsetY()));
    }else{
      textureOffsetInfoAry.push(new THREE.Vector2(0, 0));
    }
    if (obj.hasAOMap()){
      aoIntensityAry.push(obj.getAOIntensity());
    }else{
      aoIntensityAry.push(0);
    }
  }
  var orientationIndicesBufferAttribute = new THREE.InstancedBufferAttribute(new Float32Array(orientationIndices), 1);
  var alphaIndicesBufferAttribute = new THREE.InstancedBufferAttribute(new Float32Array(alphaIndices), 1);
  var scaleIndicesBufferAttribute = new THREE.InstancedBufferAttribute(new Float32Array(scaleIndices), 1);
  var affectedByLightBufferAttribute = new THREE.InstancedBufferAttribute(new Float32Array(affectedByLightAry), 1);
  orientationIndicesBufferAttribute.setDynamic(false);
  alphaIndicesBufferAttribute.setDynamic(false);
  scaleIndicesBufferAttribute.setDynamic(false);
  affectedByLightBufferAttribute.setDynamic(false);
  this.mesh.geometry.addAttribute("orientationIndex", orientationIndicesBufferAttribute);
  this.mesh.geometry.addAttribute("alphaIndex", alphaIndicesBufferAttribute);
  this.mesh.geometry.addAttribute("scaleIndex", scaleIndicesBufferAttribute);
  this.mesh.geometry.addAttribute("affectedByLight", affectedByLightBufferAttribute);
  macroHandler.injectMacro("AUTO_INSTANCE_ORIENTATION_ARRAY_SIZE "+(objCount * 2), this.mesh.material, true, false);
  macroHandler.injectMacro("AUTO_INSTANCE_ALPHA_ARRAY_SIZE "+objCount, this.mesh.material, true, false);
  macroHandler.injectMacro("AUTO_INSTANCE_SCALE_ARRAY_SIZE "+objCount, this.mesh.material, true, false);
  macroHandler.injectMacro("AUTO_INSTANCE_EMISSIVE_INTENSITY_ARRAY_SIZE "+objCount, this.mesh.material, true, false);
  macroHandler.injectMacro("AUTO_INSTANCE_EMISSIVE_COLOR_ARRAY_SIZE "+objCount, this.mesh.material, true, false);
  macroHandler.injectMacro("AUTO_INSTANCE_DISPLACEMENT_INFO_ARRAY_SIZE "+objCount, this.mesh.material, true, false);
  macroHandler.injectMacro("AUTO_INSTANCE_TEXTURE_OFFSET_INFO_ARRAY_SIZE "+objCount, this.mesh.material, true, false);
  macroHandler.injectMacro("AUTO_INSTANCE_AO_INTENSITY_ARRAY_SIZE "+objCount, this.mesh.material, true, false);
  if (this.affectedByLight){
    this.setAffectedByLight(true);
  }
  this.mesh.material.uniforms.autoInstanceOrientationArray = new THREE.Uniform(orientationAry);
  this.mesh.material.uniforms.autoInstanceAlphaArray = new THREE.Uniform(alphaAry);
  this.mesh.material.uniforms.autoInstanceScaleArray = new THREE.Uniform(scaleAry);
  this.mesh.material.uniforms.autoInstanceEmissiveIntensityArray = new THREE.Uniform(emissiveIntensityAry);
  this.mesh.material.uniforms.autoInstanceEmissiveColorArray = new THREE.Uniform(emissiveColorAry);
  this.mesh.material.uniforms.autoInstanceDisplacementInfoArray = new THREE.Uniform(displacementInfoAry);
  this.mesh.material.uniforms.autoInstanceTextureOffsetInfoArray = new THREE.Uniform(textureOffsetInfoAry);
  this.mesh.material.uniforms.autoInstanceDisplacementTextureOffsetInfoArray = new THREE.Uniform(displacementTextureOffsetInfoAry);
  this.mesh.material.uniforms.autoInstanceAOIntensityArray = new THREE.Uniform(aoIntensityAry);
  if (hasColorizableMember){
    macroHandler.injectMacro("AUTO_INSTANCE_FORCED_COLOR_ARRAY_SIZE "+(objCount), this.mesh.material, true, false);
    macroHandler.injectMacro("AUTO_INSTANCE_HAS_COLORIZABLE_MEMBER", this.mesh.material, true, true);
    var forcedColorIndicesBufferAttribute = new THREE.InstancedBufferAttribute(new Float32Array(forcedColorIndices), 1);
    forcedColorIndicesBufferAttribute.setDynamic(false);
    this.mesh.geometry.addAttribute("forcedColorIndex", forcedColorIndicesBufferAttribute);
    this.mesh.material.uniforms.autoInstanceForcedColorArray = new THREE.Uniform(forcedColorAry);
  }
  for (var objName in this.objects){
    var obj = this.objects[objName];
    if (obj.hasCustomPrecision){
      this.useCustomShaderPrecision(obj.customPrecision);
    }else{
      this.useCustomShaderPrecision(shaderPrecisionHandler.precisions[shaderPrecisionHandler.types.BASIC_MATERIAL]);
    }
    break;
  }

  this.compressGeometry();
  this.compressMaterial();
}

AutoInstancedObject.prototype.compressGeometry = function(){
  var compressableAttributes = [
    "quaternion", "alpha" , "color", "textureInfo", "textureMatrixInfo",
    "diffuseUV", "emissiveIntensity", "emissiveColor", "emissiveUV",
    "aoIntensity", "aoUV", "displacementInfo", "displacementUV", "alphaUV",
    "affectedByLight", "displacementTextureMatrixInfo"
  ];

  macroHandler.compressAttributes(this.mesh, compressableAttributes);
}

AutoInstancedObject.prototype.compressMaterial = function(){
  // compress ao intensity ary
  if (!this.pseudoObjectGroup.hasAOMap()){
    delete this.mesh.material.uniforms.autoInstanceAOIntensityArray;
    macroHandler.removeUniform(this.mesh.material, "autoInstanceAOIntensityArray");
  }else{
    delete this.mesh.material.uniforms.totalAOIntensity;
    macroHandler.removeUniform(this.mesh.material, "totalAOIntensity");
  }

  // compress emissiveColor and emissiveIntensity ary
  if (!this.pseudoObjectGroup.hasEmissiveMap()){
    delete this.mesh.material.uniforms.autoInstanceEmissiveColorArray;
    delete this.mesh.material.uniforms.autoInstanceEmissiveIntensityArray;
    macroHandler.removeUniform(this.mesh.material, "autoInstanceEmissiveColorArray");
    macroHandler.removeUniform(this.mesh.material, "autoInstanceEmissiveIntensityArray");
  }else{
    delete this.mesh.material.uniforms.totalEmissiveColor;
    delete this.mesh.material.uniforms.totalEmissiveIntensity;
    macroHandler.removeUniform(this.mesh.material, "totalEmissiveColor");
    macroHandler.removeUniform(this.mesh.material, "totalEmissiveIntensity");
  }

  // compress displacement info ary
  if (!this.pseudoObjectGroup.hasDisplacementMap()){
    delete this.mesh.material.uniforms.autoInstanceDisplacementInfoArray;
    delete this.mesh.material.uniforms.autoInstanceDisplacementTextureOffsetInfoArray;
    macroHandler.removeUniform(this.mesh.material, "autoInstanceDisplacementInfoArray");
    macroHandler.removeUniform(this.mesh.material, "autoInstanceDisplacementTextureOffsetInfoArray");
  }else{
    delete this.mesh.material.uniforms.totalDisplacementInfo;
    macroHandler.removeUniform(this.mesh.material, "totalDisplacementInfo");
  }

  // compress texture offset info ary
  if (!this.pseudoObjectGroup.hasTexture){
    delete this.mesh.material.uniforms.autoInstanceTextureOffsetInfoArray;
    macroHandler.removeUniform(this.mesh.material, "autoInstanceTextureOffsetInfoArray");
  }else{
    delete this.mesh.material.uniforms.totalTextureOffset;
    macroHandler.removeUniform(this.mesh.material, "totalTextureOffset");
  }

  // compress scale ary
  var hasScaleAnimation = false;
  for (var objName in this.pseudoObjectGroup.group){
    var obj = this.pseudoObjectGroup.group[objName];
    for (var animName in obj.animations){
      if (obj.animations[animName].isObjectScaleAnimation()){
        hasScaleAnimation = true;
        break;
      }
    }
  }
  if (!hasScaleAnimation){
    delete this.mesh.material.uniforms.autoInstanceScaleArray;
    macroHandler.removeUniform(this.mesh.material, "autoInstanceScaleArray");
    macroHandler.injectMacro("AUTO_INSTANCE_SKIP_SCALE", this.mesh.material, true, false);
  }

  // delete totalAlpha
  delete this.mesh.material.uniforms.totalAlpha;
  macroHandler.removeMacro(this.mesh.material, "totalAlpha");
}

AutoInstancedObject.prototype.setFog = function(){
  if (!this.mesh.material.uniforms.fogInfo){
    macroHandler.injectMacro("HAS_FOG", this.mesh.material, false, true);
    this.mesh.material.uniforms.fogInfo = GLOBAL_FOG_UNIFORM;
  }
  if (fogHandler.isFogBlendingWithSkybox()){
    if (!this.mesh.material.uniforms.cubeTexture){
      macroHandler.injectMacro("HAS_SKYBOX_FOG", this.mesh.material, true, true);
      this.mesh.material.uniforms.worldMatrix = new THREE.Uniform(this.mesh.matrixWorld);
      this.mesh.material.uniforms.cubeTexture = GLOBAL_CUBE_TEXTURE_UNIFORM;
      this.mesh.material.uniforms.cameraPosition = GLOBAL_CAMERA_POSITION_UNIFORM;
    }
  }
  this.mesh.material.needsUpdate = true;
}

AutoInstancedObject.prototype.removeFog = function(){
  macroHandler.removeMacro("HAS_FOG", this.mesh.material, false, true);
  macroHandler.removeMacro("HAS_SKYBOX_FOG", this.mesh.material, true, true);
  delete this.mesh.material.uniforms.fogInfo;
  delete this.mesh.material.uniforms.cubeTexture;
  delete this.mesh.material.uniforms.worldMatrix;
  delete this.mesh.material.uniforms.cameraPosition;
  this.mesh.material.needsUpdate = true;
}

AutoInstancedObject.prototype.getRegisteredSceneName = function(){
  for (var objName in this.objects){
    return this.objects[objName].registeredSceneName;
  }
}

var AutoInstancingHandler = function(){
  this.maxBatchObjectSize = parseInt((MAX_VERTEX_UNIFORM_VECTORS - 50) / 3);
}

AutoInstancingHandler.prototype.getObjectKey = function(obj){
  var geomKey = obj.mesh.geometry.uuid;
  var blendingKey = obj.blendingMode;
  if (!blendingKey){
    blendingKey = "NORMAL_BLENDING";
  }
  var shaderPrecisionKey = shaderPrecisionHandler.precisions[shaderPrecisionHandler.types.BASIC_MATERIAL];
  if (obj.hasCustomPrecision){
    shaderPrecisionKey = obj.customPrecision;
  }
  return geomKey + PIPE + obj.registeredSceneName + PIPE + blendingKey + PIPE + shaderPrecisionKey;
}

AutoInstancingHandler.prototype.handle = function(){
  if (!INSTANCING_SUPPORTED){
    return;
  }
  autoInstancedObjects = new Object();
  var objectsByGeometryID = new Object();
  var countersByObjectKey = new Object();
  for (var objName in addedObjects){
    var obj = addedObjects[objName];
    if (!obj.isFPSWeapon && (obj.isChangeable || (!obj.noMass && obj.physicsBody.mass > 0))){
      var objKey = this.getObjectKey(obj);
      if (typeof countersByObjectKey[objKey] == UNDEFINED){
        countersByObjectKey[objKey] = 0;
      }
      var key = objKey + PIPE + countersByObjectKey[objKey];
      if (!objectsByGeometryID[key]){
        objectsByGeometryID[key] = [];
      }else if (objectsByGeometryID[key].length > this.maxBatchObjectSize){
        countersByObjectKey[objKey] ++;
        key = objKey + PIPE + (countersByObjectKey[objKey]);
        objectsByGeometryID[key] = [];
      }
      objectsByGeometryID[key].push(obj);
    }
  }
  var ctr = 0;
  for (var key in objectsByGeometryID){
    var ary = objectsByGeometryID[key];
    if (ary.length > 1){
      var pseudoGroup = new Object();
      for (var i = 0; i<ary.length; i++){
        pseudoGroup[ary[i].name] = ary[i];
        scene.remove(ary[i].mesh);
      }
      var autoInstancedObject = new AutoInstancedObject("autoInstancedObject_"+(ctr++), pseudoGroup);
      autoInstancedObject.init();
      scene.add(autoInstancedObject.mesh);
      autoInstancedObject.mesh.visible = false;
      autoInstancedObjects[autoInstancedObject.name] = autoInstancedObject;
      sceneHandler.onAutoInstancedObjectCreation(autoInstancedObject);
    }
  }
}

AutoInstancingHandler.prototype.reset = function(){
  if (!INSTANCING_SUPPORTED){
    return;
  }
  for (var autoInstancedObjectName in autoInstancedObjects){
    var autoInstancedObject = autoInstancedObjects[autoInstancedObjectName];
    scene.remove(autoInstancedObject.mesh);
    for (var objName in autoInstancedObject.objects){
      scene.add(autoInstancedObject.objects[objName].mesh);
      delete autoInstancedObject.objects[objName].autoInstancedParent;
    }
  }
  autoInstancedObjects = new Object();
}

var Renderer = function(){
  if (!isDeployment){
    this.webglRenderer = new THREE.WebGLRenderer({canvas: canvas});
  }else{
    this.webglRenderer = new THREE.WebGLRenderer({canvas: canvas, antialias: ENABLE_ANTIALIAS});
  }
}

Renderer.prototype.initEffects = function(){
  this.effects = {bloom: new Bloom()};
  bloom = this.effects.bloom;
  this.mandatoryEffectMethods = ["setSize", "setViewport", "setPixelRatio", "render", "showConfigurations", "hideConfigurations", "export", "load", "reset"];
  for (var effectName in this.effects){
    for (var i = 0; i<this.mandatoryEffectMethods.length; i++){
      if (!this.effects[effectName][this.mandatoryEffectMethods[i]]){
        console.error("[!] Renderer error: effect "+effectName+" does not have "+this.mandatoryEffectMethods[i]+" implemented.");
      }
    }
  }
}

Renderer.prototype.render = function(scene, camera){
  if (this.bloomOn){
    this.effects.bloom.render();
    return;
  }
  this.webglRenderer.render(scene, camera);
}

Renderer.prototype.setViewport = function(x, y, z, w){
  this.webglRenderer.setViewport(x, y, z, w);
  for (var effectName in this.effects){
    this.effects[effectName].setViewport(x, y, z, w);
  }
}

Renderer.prototype.getCurrentViewport = function(){
  return this.webglRenderer.getCurrentViewport();
}

Renderer.prototype.isHighPrecisionSupported = function(){
  return !(
    this.webglRenderer.context.getShaderPrecisionFormat(this.webglRenderer.context.VERTEX_SHADER, this.webglRenderer.context.HIGH_FLOAT).precision <= 0 ||
    this.webglRenderer.context.getShaderPrecisionFormat(this.webglRenderer.context.FRAGMENT_SHADER, this.webglRenderer.context.HIGH_FLOAT).precision <= 0
  );
}

Renderer.prototype.isInstancingSupported = function(){
  return (!(this.webglRenderer.context.getExtension("ANGLE_instanced_arrays") == null));
}

Renderer.prototype.isASTCSupported = function(){
  return (!(this.webglRenderer.context.getExtension("WEBGL_compressed_texture_astc") == null));
}

Renderer.prototype.isS3TCSupported = function(){
  return (!(this.webglRenderer.context.getExtension("WEBGL_compressed_texture_s3tc") == null));
}

Renderer.prototype.isPVRTCSupported = function(){
  return (!(this.webglRenderer.context.getExtension("WEBGL_compressed_texture_pvrtc") == null));
}

Renderer.prototype.isVertexShaderTextureFetchSupported = function(){
  return (this.webglRenderer.context.getParameter(this.webglRenderer.context.MAX_VERTEX_TEXTURE_IMAGE_UNITS) > 0);
}

Renderer.prototype.getMaxVertexAttribs = function(){
  return this.webglRenderer.capabilities.maxAttributes;
}

Renderer.prototype.getMaxVertexUniformVectors = function(){
  return this.webglRenderer.context.getParameter(this.webglRenderer.context.MAX_VERTEX_UNIFORM_VECTORS);
}

Renderer.prototype.getBoundingClientRect = function(){
  return this.webglRenderer.domElement.getBoundingClientRect();
}

Renderer.prototype.setSize = function(width, height){
  this.webglRenderer.setSize(width, height);
  for (var effectName in this.effects){
    this.effects[effectName].setSize(width, height);
  }
}

Renderer.prototype.setPixelRatio = function(ratio){
  this.webglRenderer.setPixelRatio(ratio);
  GLOBAL_SCREEN_RESOLUTION_UNIFORM.value = ratio;
  for (var effectName in this.effects){
    this.effects[effectName].setPixelRatio(ratio);
  }
}

Renderer.prototype.getContext = function(){
  return this.webglRenderer.context;
}

var Bloom = function(){
  this.configurations = {
    blurStepCount: 5,
    threshold: 1,
    bloomStrength: 2,
    exposure: 1,
    gamma: 1,
    tapTypes: [13, 13, 13, 13, 13],
    bloomFactors: [1, 1, 1, 1, 1],
    bloomTintColors: [new THREE.Vector3(1, 1, 1), new THREE.Vector3(1, 1, 1), new THREE.Vector3(1, 1, 1), new THREE.Vector3(1, 1, 1), new THREE.Vector3(1, 1, 1)]
  }
  this.rtParameters = {minFilter: THREE.LinearFilter, magFilter: THREE.LinearFilter, format: THREE.RGBAFormat};
  this.generateDirectPass();
  this.generateBrightPass();
  this.generateBlurPass();
  this.generateCombinerPass();
  this.setBlurStepCount(this.configurations.blurStepCount);
}

Bloom.prototype.getTapTextFromTapType = function(type){
  if (type == 13){
    return "high";
  }
  if (type == 9){
    return "medium";
  }
  if (type == 5){
    return "low";
  }
  throw new Error("Unknown tap type.");
}

Bloom.prototype.reset = function(){
  renderer.bloomOn = false;
  this.setBlurStepCount(5);
  this.setThreshold(1);
  this.setBloomStrength(2);
  this.setExposure(1);
  this.setGamma(1);
  for (var i = 0; i<5; i++){
    this.setTapForLevel(i, 13);
    this.setBloomFactor(i, 1);
    this.setBloomTintColor(i, 1, 1, 1);
  }
}

Bloom.prototype.load = function(configs){
  this.setBlurStepCount(configs.blurStepCount);
  this.setThreshold(configs.threshold);
  this.setBloomStrength(configs.bloomStrength);
  this.setExposure(configs.exposure);
  this.setGamma(configs.gamma);
  for (var i = 0; i<5; i++){
    this.setTapForLevel(i, configs.tapTypes[i]);
    this.setBloomFactor(i, configs.bloomFactors[i]);
    var curBloomTintColor = configs.bloomTintColors[i];
    this.setBloomTintColor(i, curBloomTintColor.x, curBloomTintColor.y, curBloomTintColor.z);
  }
  renderer.bloomOn = configs.isOn;
}

Bloom.prototype.export = function(){
  var exportObj = new Object();
  exportObj.isOn = renderer.bloomOn;
  for (var config in this.configurations){
    exportObj[config] = this.configurations[config];
  }
  return exportObj;
}

Bloom.prototype.showConfigurations = function(){
  guiHandler.show(guiHandler.guiTypes.BLOOM);
  guiHandler.bloomParameters["Active"] = renderer.bloomOn;
  if (typeof guiHandler.bloomParameters["Active"] == UNDEFINED){
    guiHandler.bloomParameters["Active"] = false;
  }
  guiHandler.bloomParameters["Threshold"] = this.configurations.threshold;
  guiHandler.bloomParameters["Strength"] = this.configurations.bloomStrength;
  guiHandler.bloomParameters["Exposure"] = this.configurations.exposure;
  guiHandler.bloomParameters["Gamma"] = this.configurations.gamma;
  guiHandler.bloomParameters["BlurStepAmount"] = this.configurations.blurStepCount;
  for (var i=0; i<5; i++){
    guiHandler.bloomParameters["BlurPass"+(i+1)]["Factor"] = this.configurations.bloomFactors[i];
    guiHandler.bloomParameters["BlurPass"+(i+1)]["Color"] = "#" + (REUSABLE_COLOR.setRGB(this.configurations.bloomTintColors[i].x, this.configurations.bloomTintColors[i].y, this.configurations.bloomTintColors[i].z).getHexString());
    guiHandler.bloomParameters["BlurPass"+(i+1)]["Quality"] = this.getTapTextFromTapType(this.configurations.tapTypes[i]);
    guiHandler.enableController(guiHandler["blurPassFactorController"+(i+1)]);
    guiHandler.enableController(guiHandler["blurPassTintColorController"+(i+1)]);
    guiHandler.enableController(guiHandler["blurPassTapController"+(i+1)]);
  }
  for (var i = this.configurations.blurStepCount; i < 5; i++){
    guiHandler.disableController(guiHandler["blurPassFactorController"+(i+1)]);
    guiHandler.disableController(guiHandler["blurPassTintColorController"+(i+1)]);
    guiHandler.disableController(guiHandler["blurPassTapController"+(i+1)]);
  }
  this.configurationsOpen = true;
}

Bloom.prototype.hideConfigurations = function(){
  guiHandler.hide(guiHandler.guiTypes.BLOOM);
  this.configurationsOpen = false;
}

Bloom.prototype.setBloomTintColor = function(levelIndex, r, g, b){
  this.configurations.bloomTintColors[levelIndex].set(r, g, b);
}

Bloom.prototype.setBloomFactor = function(levelIndex, factor){
  this.configurations.bloomFactors[levelIndex] = factor;
}

Bloom.prototype.setGamma = function(gamma){
  this.configurations.gamma = gamma;
  this.combinerMaterial.uniforms.gamma.value = gamma;
}

Bloom.prototype.setExposure = function(exposure){
  this.configurations.exposure = exposure;
  this.combinerMaterial.uniforms.exposure.value = exposure;
}

Bloom.prototype.setBlurStepCount = function(stepCount){
  if (stepCount > 5){
    throw new Error("[!] Bloom.setBlurStepCount error: Max alloed stepCount is 5.");
  }
  if (stepCount < 1){
    stepCount = 1
  }
  this.configurations.blurStepCount = stepCount;
  for (var i = 0; i<5; i++){
    var macro = "BLUR_STEP_"+(i+1)+"_ACTIVE";
    macroHandler.removeMacro(macro, this.combinerMaterial, false, true);
  }
  for (var i = 0; i<stepCount; i++){
    var macro = "BLUR_STEP_"+(i+1)+"_ACTIVE";
    macroHandler.injectMacro(macro, this.combinerMaterial, false, true);
  }
}

Bloom.prototype.setBloomStrength = function(strength){
  this.combinerMaterial.uniforms.bloomStrength.value = strength;
  this.configurations.bloomStrength = strength;
}

Bloom.prototype.setThreshold = function(threshold){
  this.configurations.threshold = threshold;
  this.brightPassMaterial.uniforms.threshold.value = threshold;
}

Bloom.prototype.setTapForLevel = function(levelIndex, tap){
  this.configurations.tapTypes[levelIndex] = tap;
}

Bloom.prototype.setBlurTap = function(tap){
  if (tap == 5){
    this.blurPassMaterial.uniforms.numberOfTap.value = -10;
    return;
  }
  if (tap == 9){
    this.blurPassMaterial.uniforms.numberOfTap.value = 5;
    return;
  }
  if (tap == 13){
    this.blurPassMaterial.uniforms.numberOfTap.value = 20;
    return;
  }
  throw new Error("[!] Bloom.setBlurTap error: Undefined tap.");
}

Bloom.prototype.setBlurDirection = function(isX){
  if (isX){
    this.blurPassMaterial.uniforms.direction.value = this.blurPassDirectionX;
  }else{
    this.blurPassMaterial.uniforms.direction.value = this.blurPassDirectionY;
  }
}

Bloom.prototype.combinerPass = function(){
  renderer.webglRenderer.render(this.combinerScene, orthographicCamera);
}

Bloom.prototype.blurPass = function(){
  this.blurPassMaterial.uniforms.inputTexture.value = this.brightTarget.texture;
  this.blurPassMaterial.uniforms.resolution.value.set(this.brightTarget.width, this.brightTarget.height);
  for (var i = 0; i <this.configurations.blurStepCount; i++){
    this.setBlurTap(this.configurations.tapTypes[i]);
    this.setBlurDirection(true);
    renderer.webglRenderer.render(this.blurPassScene, orthographicCamera, this.horizontalBlurTargets[i]);
    var rt = this.horizontalBlurTargets[i];
    this.blurPassMaterial.uniforms.inputTexture.value = rt.texture;
    this.blurPassMaterial.uniforms.resolution.value.set(rt.width, rt.height);
    this.setBlurDirection(false);
    renderer.webglRenderer.render(this.blurPassScene, orthographicCamera, this.verticalBlurTargets[i]);
    rt = this.verticalBlurTargets[i];
    this.blurPassMaterial.uniforms.inputTexture.value = rt.texture;
    this.blurPassMaterial.uniforms.resolution.value.set(rt.width, rt.height);
  }
}

Bloom.prototype.brightPass = function(){
  renderer.webglRenderer.render(this.brightPassScene, orthographicCamera, this.brightTarget);
}

Bloom.prototype.directPass = function(){
  renderer.webglRenderer.render(scene, camera, this.sceneTarget);
}

Bloom.prototype.generateCombinerPass = function(){
  this.combinerMaterial = new THREE.RawShaderMaterial({
    vertexShader: ShaderContent.bloomCombinerVertexShader,
    fragmentShader: ShaderContent.bloomCombinerFragmentShader,
    uniforms:{
      modelViewMatrix: new THREE.Uniform(),
      projectionMatrix: new THREE.Uniform(orthographicCamera.projectionMatrix),
      sceneTexture: new THREE.Uniform(this.sceneTarget.texture),
      blurTexture1: new THREE.Uniform(this.verticalBlurTargets[0].texture),
      blurTexture2: new THREE.Uniform(this.verticalBlurTargets[1].texture),
      blurTexture3: new THREE.Uniform(this.verticalBlurTargets[2].texture),
      blurTexture4: new THREE.Uniform(this.verticalBlurTargets[3].texture),
      blurTexture5: new THREE.Uniform(this.verticalBlurTargets[4].texture),
      bloomStrength: new THREE.Uniform(this.configurations.bloomStrength),
      exposure: new THREE.Uniform(this.configurations.exposure),
      gamma: new THREE.Uniform(this.configurations.gamma),
      bloomFactors: new THREE.Uniform(this.configurations.bloomFactors),
      bloomTintColors: new THREE.Uniform(this.configurations.bloomTintColors)
    }
  });
  this.combinerQuad = new THREE.Mesh(REUSABLE_QUAD_GEOMETRY, this.combinerMaterial);
  this.combinerMaterial.uniforms.modelViewMatrix.value = this.combinerQuad.modelViewMatrix;
  this.combinerScene = new THREE.Scene();
  this.combinerScene.add(this.combinerQuad);
}

Bloom.prototype.generateDirectPass = function(){
  this.sceneTarget = new THREE.WebGLRenderTarget(renderer.getCurrentViewport().z, renderer.getCurrentViewport().w, this.rtParameters);
  this.sceneTarget.texture.generateMipmaps = false;
}

Bloom.prototype.generateBlurPass = function(){
  this.blurPassDirectionX = new THREE.Vector2(1, 0);
  this.blurPassDirectionY = new THREE.Vector2(0, 1);
  this.blurPassMaterial = new THREE.RawShaderMaterial({
    vertexShader: ShaderContent.bloomBlurPassVertexShader,
    fragmentShader: ShaderContent.bloomBlurPassFragmentShader,
    uniforms: {
      modelViewMatrix: new THREE.Uniform(),
      projectionMatrix: new THREE.Uniform(orthographicCamera.projectionMatrix),
      inputTexture: new THREE.Uniform(),
      numberOfTap: new THREE.Uniform(20),
      resolution: new THREE.Uniform(new THREE.Vector2()),
      direction: new THREE.Uniform(new THREE.Vector2()),
    }
  });
  this.blurPassQuad = new THREE.Mesh(REUSABLE_QUAD_GEOMETRY, this.blurPassMaterial);
  this.blurPassMaterial.uniforms.modelViewMatrix.value = this.blurPassQuad.modelViewMatrix;
  this.blurPassScene = new THREE.Scene();
  this.blurPassScene.add(this.blurPassQuad);
  this.horizontalBlurTargets = [], this.verticalBlurTargets = [];
  var coef = 2;
  for (var i = 0; i<this.configurations.blurStepCount; i++){
    var rt1 = new THREE.WebGLRenderTarget(renderer.getCurrentViewport().z / coef, renderer.getCurrentViewport().w / coef, this.rtParameters);
    var rt2 = new THREE.WebGLRenderTarget(renderer.getCurrentViewport().z / coef, renderer.getCurrentViewport().w / coef, this.rtParameters);
    rt1.texture.generateMipmaps = false;
    rt2.texture.generateMipmaps = false;
    this.horizontalBlurTargets.push(rt1);
    this.verticalBlurTargets.push(rt2);
    coef = coef * 2;
  }
}

Bloom.prototype.generateBrightPass = function(){
  this.brightPassMaterial = new THREE.RawShaderMaterial({
    vertexShader: ShaderContent.bloomBrightPassVertexShader,
    fragmentShader: ShaderContent.bloomBrightPassFragmentShader,
    uniforms: {
      modelViewMatrix: new THREE.Uniform(),
      projectionMatrix: new THREE.Uniform(orthographicCamera.projectionMatrix),
      sceneTexture: new THREE.Uniform(),
      threshold: new THREE.Uniform(this.configurations.threshold)
    }
  });
  this.brightPassQuad = new THREE.Mesh(REUSABLE_QUAD_GEOMETRY, this.brightPassMaterial);
  this.brightPassMaterial.uniforms.modelViewMatrix.value = this.brightPassQuad.modelViewMatrix;
  this.brightPassMaterial.uniforms.sceneTexture.value = this.sceneTarget.texture;
  this.brightPassScene = new THREE.Scene();
  this.brightPassScene.add(this.brightPassQuad);
  this.brightTarget = new THREE.WebGLRenderTarget(renderer.getCurrentViewport().z / 2, renderer.getCurrentViewport().w / 2, this.rtParameters);
  this.brightTarget.texture.generateMipmaps = false;
}

Bloom.prototype.setSize = function(width, height){
  this.sceneTarget.setSize(width, height);
  this.brightTarget.setSize(width / 2, height / 2);
  var coef = 2;
  for (var i = 0; i<this.configurations.blurStepCount; i++){
    this.horizontalBlurTargets[i].setSize(width/coef, height/coef);
    this.verticalBlurTargets[i].setSize(width/coef, height/coef);
    coef = coef * 2;
  }
}

Bloom.prototype.setViewport = function(x, y, z, w){
  this.setSize(z, w);
}

Bloom.prototype.setPixelRatio = function(ratio){
  this.setSize(renderer.getCurrentViewport().z, renderer.getCurrentViewport().w);
}

Bloom.prototype.render = function(){
  this.directPass();
  this.brightPass();
  this.blurPass();
  this.combinerPass();
}

