AnimationHandler.prototype.linearFunc = function(curTime, startVal, changeInVal, totalTime){
  return (changeInVal * curTime / totalTime) + startVal;
}

AnimationHandler.prototype.quadEaseInFunc = function(curTime, startVal, changeInVal, totalTime){
  return changeInVal * (curTime/=totalTime) * curTime + startVal;
}

AnimationHandler.prototype.quadEaseOutFunc = function(curTime, startVal, changeInVal, totalTime){
  return -changeInVal * (curTime/=totalTime) * (curTime-2) + startVal;
}

AnimationHandler.prototype.quadEaseInOutFunc = function(curTime, startVal, changeInVal, totalTime){
  if ((curTime/=totalTime/2) < 1) return changeInVal/2*curTime*curTime + startVal;
  return -changeInVal/2 * ((--curTime)*(curTime-2) - 1) + startVal;
}

AnimationHandler.prototype.cubicEaseInFunc = function(curTime, startVal, changeInVal, totalTime){
  return changeInVal*(curTime/=totalTime)*curTime*curTime + startVal;
}

AnimationHandler.prototype.cubicEaseOutFunc = function(curTime, startVal, changeInVal, totalTime){
  return changeInVal*((curTime=curTime/totalTime-1)*curTime*curTime + 1) + startVal;
}

AnimationHandler.prototype.cubicEaseInOutFunc = function(curTime, startVal, changeInVal, totalTime){
  if ((curTime/=totalTime/2) < 1) return changeInVal/2*curTime*curTime*curTime + startVal;
  return changeInVal/2*((curTime-=2)*curTime*curTime + 2) + startVal;
}

AnimationHandler.prototype.quartEaseInFunc = function(curTime, startVal, changeInVal, totalTime){
  return changeInVal*(curTime/=totalTime)*curTime*curTime*curTime + startVal;
}

AnimationHandler.prototype.quartEaseOutFunc = function(curTime, startVal, changeInVal, totalTime){
  return -changeInVal * ((curTime=curTime/totalTime-1)*curTime*curTime*curTime - 1) + startVal;
}

AnimationHandler.prototype.quartEaseInOutFunc = function(curTime, startVal, changeInVal, totalTime){
  if ((curTime/=totalTime/2) < 1) return changeInVal/2*curTime*curTime*curTime*curTime + startVal;
  return -changeInVal/2 * ((curTime-=2)*curTime*curTime*curTime - 2) + startVal;
}

AnimationHandler.prototype.quintEaseInFunc = function(curTime, startVal, changeInVal, totalTime){
  return changeInVal*(curTime/=totalTime)*curTime*curTime*curTime*curTime + startVal;
}

AnimationHandler.prototype.quintEaseOutFunc = function(curTime, startVal, changeInVal, totalTime){
  return changeInVal*((curTime=curTime/totalTime-1)*curTime*curTime*curTime*curTime + 1) + startVal;
}

AnimationHandler.prototype.quintEaseInOutFunc = function(curTime, startVal, changeInVal, totalTime){
  if ((curTime/=totalTime/2) < 1) return changeInVal/2*curTime*curTime*curTime*curTime*curTime + startVal;
  return changeInVal/2*((curTime-=2)*curTime*curTime*curTime*curTime + 2) + startVal;
}

AnimationHandler.prototype.sineEaseInFunc = function(curTime, startVal, changeInVal, totalTime){
  return -changeInVal * Math.cos(curTime/totalTime * (Math.PI/2)) + changeInVal + startVal;
}

AnimationHandler.prototype.sineEaseOutFunc = function(curTime, startVal, changeInVal, totalTime){
  return changeInVal * Math.sin(curTime/totalTime * (Math.PI/2)) + startVal;
}

AnimationHandler.prototype.sineEaseInOutFunc = function(curTime, startVal, changeInVal, totalTime){
  return -changeInVal/2 * (Math.cos(Math.PI*curTime/totalTime) - 1) + startVal;
}

AnimationHandler.prototype.expoEaseInFunc = function(curTime, startVal, changeInVal, totalTime){
  return (curTime==0) ? startVal : changeInVal * Math.pow(2, 10 * (curTime/totalTime - 1)) + startVal;
}

AnimationHandler.prototype.expoEaseOutFunc = function(curTime, startVal, changeInVal, totalTime){
  return (curTime==totalTime) ? startVal+changeInVal : changeInVal * (-Math.pow(2, -10 * curTime/totalTime) + 1) + startVal;
}

AnimationHandler.prototype.expoEaseInOutFunc = function(curTime, startVal, changeInVal, totalTime){
  return (curTime==totalTime) ? startVal+changeInVal : changeInVal * (-Math.pow(2, -10 * curTime/totalTime) + 1) + startVal;
}

AnimationHandler.prototype.circEaseInFunc = function(curTime, startVal, changeInVal, totalTime){
  return -changeInVal * (Math.sqrt(1 - (curTime/=totalTime)*curTime) - 1) + startVal;
}

AnimationHandler.prototype.circEaseOutFunc = function(curTime, startVal, changeInVal, totalTime){
  return changeInVal * Math.sqrt(1 - (curTime=curTime/totalTime-1)*curTime) + startVal;
}

AnimationHandler.prototype.circEaseInOutFunc = function(curTime, startVal, changeInVal, totalTime){
  if ((curTime/=totalTime/2) < 1) return -changeInVal/2 * (Math.sqrt(1 - curTime*curTime) - 1) + startVal;
  return changeInVal/2 * (Math.sqrt(1 - (curTime-=2)*curTime) + 1) + startVal;
}

AnimationHandler.prototype.elasticEaseInFunc = function(curTime, startVal, changeInVal, totalTime){
  var s=1.70158;var p=0;var a=changeInVal;
  if (curTime==0) return startVal;  if ((curTime/=totalTime)==1) return startVal+changeInVal;  if (!p) p=totalTime*.3;
  if (a < Math.abs(changeInVal)) { a=changeInVal; var s=p/4; }
  else var s = p/(2*Math.PI) * Math.asin (changeInVal/a);
  return -(a*Math.pow(2,10*(curTime-=1)) * Math.sin( (curTime*totalTime-s)*(2*Math.PI)/p )) + startVal;
}

AnimationHandler.prototype.elasticEaseOutFunc = function(curTime, startVal, changeInVal, totalTime){
  var s=1.70158;var p=0;var a=changeInVal;
  if (curTime==0) return startVal;  if ((curTime/=totalTime)==1) return startVal+changeInVal;  if (!p) p=totalTime*.3;
  if (a < Math.abs(changeInVal)) { a=changeInVal; var s=p/4; }
  else var s = p/(2*Math.PI) * Math.asin (changeInVal/a);
  return a*Math.pow(2,-10*curTime) * Math.sin( (curTime*totalTime-s)*(2*Math.PI)/p ) + changeInVal + startVal;
}

AnimationHandler.prototype.elasticEaseInOutFunc = function(curTime, startVal, changeInVal, totalTime){
  var s=1.70158;var p=0;var a=changeInVal;
  if (curTime==0) return startVal;  if ((curTime/=totalTime/2)==2) return startVal+changeInVal;  if (!p) p=totalTime*(.3*1.5);
  if (a < Math.abs(changeInVal)) { a=changeInVal; var s=p/4; }
  else var s = p/(2*Math.PI) * Math.asin (changeInVal/a);
  if (curTime < 1) return -.5*(a*Math.pow(2,10*(curTime-=1)) * Math.sin( (curTime*totalTime-s)*(2*Math.PI)/p )) + startVal;
  return a*Math.pow(2,-10*(curTime-=1)) * Math.sin( (curTime*totalTime-s)*(2*Math.PI)/p )*.5 + changeInVal + startVal;
}

AnimationHandler.prototype.backEaseInFunc = function(curTime, startVal, changeInVal, totalTime){
  var s = 1.70158;
  return changeInVal*(curTime/=totalTime)*curTime*((s+1)*curTime - s) + startVal;
}

AnimationHandler.prototype.backEaseOutFunc = function(curTime, startVal, changeInVal, totalTime){
  var s = 1.70158;
  return changeInVal*((curTime=curTime/totalTime-1)*curTime*((s+1)*curTime + s) + 1) + startVal;
}

AnimationHandler.prototype.backEaseInOutFunc = function(curTime, startVal, changeInVal, totalTime){
  var s = 1.70158;
  if ((curTime/=totalTime/2) < 1) return changeInVal/2*(curTime*curTime*(((s*=(1.525))+1)*curTime - s)) + startVal;
  return changeInVal/2*((curTime-=2)*curTime*(((s*=(1.525))+1)*curTime + s) + 2) + startVal;
}

AnimationHandler.prototype.bounceEaseInFunc = function(curTime, startVal, changeInVal, totalTime){
  var easeOutBounceFunc = animationHandler.updateFunctionsByType[animationHandler.animationTypes.BOUNCE_EASE_OUT];
  return changeInVal - easeOutBounceFunc(totalTime-curTime, 0, changeInVal, totalTime) + startVal;
}

AnimationHandler.prototype.bounceEaseOutFunc = function(curTime, startVal, changeInVal, totalTime){
  if ((curTime/=totalTime) < (1/2.75)){
    return changeInVal*(7.5625*curTime*curTime) + startVal;
  }else if (curTime < (2/2.75)){
    return changeInVal*(7.5625*(curTime-=(1.5/2.75))*curTime + .75) + startVal;
  }else if (curTime < (2.5/2.75)){
    return changeInVal*(7.5625*(curTime-=(2.25/2.75))*curTime + .9375) + startVal;
  }else{
    return changeInVal*(7.5625*(curTime-=(2.625/2.75))*curTime + .984375) + startVal;
  }
}

AnimationHandler.prototype.bounceEaseInOutFunc = function(curTime, startVal, changeInVal, totalTime){
  var easeInBounceFunc = animationHandler.updateFunctionsByType[animationHandler.animationTypes.BOUNCE_EASE_IN];
  var easeOutBounceFunc = animationHandler.updateFunctionsByType[animationHandler.animationTypes.BOUNCE_EASE_OUT];
  if (curTime < totalTime/2) return easeInBounceFunc(curTime*2, 0, changeInVal, totalTime) * .5 + startVal;
  return easeOutBounceFunc(curTime*2-totalTime, 0, changeInVal, totalTime) * .5 + changeInVal*.5 + startVal;
}

var Animation = function(name, type, attachedObject, description, rewind, repeat){
  this.name = name;
  this.type = type;
  this.attachedObject = attachedObject;
  this.description = JSON.parse(JSON.stringify(description));
  this.rewind = rewind;
  this.updateFunction = animationHandler.updateFunctionsByType[type];
  this.actionFunction = animationHandler.actionFunctionsByType[description.action];
  this.totalTimeInSeconds = this.description.totalTimeInSeconds;
  this.repeat = repeat;
  this.params = {object: this.attachedObject};
  if (description.action == animationHandler.actionTypes.OBJECT.EMISSIVE_COLOR || description.action == animationHandler.actionTypes.TEXT.TEXT_COLOR || description.action == animationHandler.actionTypes.TEXT.BACKGROUND_COLOR || description.action == animationHandler.actionTypes.SPRITE.COLOR){
    this.changeInValue = 1;
    this.params.targetColor = new THREE.Color(description.targetColor);
    this.params.sourceColor = new THREE.Color();
  }else if (description.action == animationHandler.actionTypes.TEXT.TYPING){
    this.changeInValue = attachedObject.text.length + 1;
    this.params.sourceText = attachedObject.text;
  }else if (description.action == animationHandler.actionTypes.SPRITE.TARGET_POSITION_X){
    this.changeInValue = 1;
    this.params.sourcePosition = attachedObject.marginPercentX;
    this.params.targetPosition = description.targetPosition;
  }else if (description.action == animationHandler.actionTypes.SPRITE.TARGET_POSITION_Y){
    this.changeInValue = 1;
    this.params.sourcePosition = attachedObject.marginPercentY;
    this.params.targetPosition = description.targetPosition;
  }else if (description.action == animationHandler.actionTypes.SPRITE.TARGET_ROTATION){
    this.changeInValue = 1;
    this.params.sourceRotation = attachedObject.mesh.material.uniforms.rotationAngle.value;
    this.params.targetRotation = description.targetRotation;
  }else if (description.action == animationHandler.actionTypes.SPRITE.TARGET_SCALE_X){
    this.changeInValue = 1;
    this.params.sourceScale = attachedObject.mesh.material.uniforms.scale.value.x;
    this.params.targetScale = description.targetScale;
    if (!(typeof attachedObject.fixedWidth == UNDEFINED)){
      this.params.sourceScale = attachedObject.fixedWidth
    }
  }else if (description.action == animationHandler.actionTypes.SPRITE.TARGET_SCALE_Y){
    this.changeInValue = 1;
    this.params.sourceScale = attachedObject.mesh.material.uniforms.scale.value.y;
    this.params.targetScale = description.targetScale;
    if (!(typeof attachedObject.fixedHeight == UNDEFINED)){
      this.params.sourceScale = attachedObject.fixedHeight
    }
  }else{
    this.changeInValue = this.description.changeInValue;
  }
  animationHandler.assignUUIDToAnimation(this);
  this.savedState = {
    initialValue: 0, tick: 0, increaseTick: false, totalTranslationX: 0, totalTranslationY: 0, totalTranslationZ: 0
  }
  this.isActive = false;
  this.animationState = ANIMATION_STATE_NOT_RUNNING;
}

Animation.prototype.isObjectScaleAnimation = function(){
  var actionType = this.description.action;
  return actionType == animationHandler.actionTypes.OBJECT.SCALE_X ||
         actionType == animationHandler.actionTypes.OBJECT.SCALE_Y ||
         actionType == animationHandler.actionTypes.OBJECT.SCALE_Z;
}

Animation.prototype.isCustomDisplacementAnimation = function(){
  var actionType = this.description.action;
  return actionType == animationHandler.actionTypes.OBJECT.DISP_TEXTURE_OFFSET_X ||
         actionType == animationHandler.actionTypes.OBJECT.DISP_TEXTURE_OFFSET_Y;
}

Animation.prototype.isDisplacementAnimation = function(){
  var actionType = this.description.action;
  return this.isCustomDisplacementAnimation() ||
          actionType == animationHandler.actionTypes.OBJECT.DISPLACEMENT_SCALE ||
          actionType == animationHandler.actionTypes.OBJECT.DISPLACEMENT_BIAS;

}

Animation.prototype.isEmissiveIntensityAnimation = function(){
  var actionType = this.description.action;
  return actionType == animationHandler.actionTypes.OBJECT.EMISSIVE_INTENSITY;
}

Animation.prototype.isEmissiveColorAnimation = function(){
  var actionType = this.description.action;
  return actionType == animationHandler.actionTypes.OBJECT.EMISSIVE_COLOR;
}

Animation.prototype.isEmissiveAnimation = function(){
  return this.isEmissiveIntensityAnimation() || this.isEmissiveColorAnimation();
}

Animation.prototype.isAOAnimation = function(){
  var actionType = this.description.action;
  return actionType == animationHandler.actionTypes.OBJECT.AO_INTENSITY;
}

Animation.prototype.isTextureOffsetAnimation = function(){
  var actionType = this.description.action;
  return actionType == animationHandler.actionTypes.OBJECT.TEXTURE_OFFSET_X ||
          actionType == animationHandler.actionTypes.OBJECT.TEXTURE_OFFSET_Y;
}

Animation.prototype.isTextureAnimation = function(){
  var actionType = this.description.action;
  return this.isEmissiveAnimation() || this.isDisplacementAnimation() || this.isTextureOffsetAnimation() || this.isAOAnimation();
}

Animation.prototype.copyWithAnotherObject = function(obj){
  return new Animation(this.name, this.type, obj, this.description, this.rewind, this.repeat);
}

Animation.prototype.restore = function(){
  this.initialValue = this.savedState.initialValue;
  this.tick = this.savedState.tick;
  this.increaseTick = this.savedState.increaseTick;
  if (this.description.action == animationHandler.actionTypes.OBJECT.TRANSLATE_X){
    this.params.totalTranslationX = this.savedState.totalTranslationX;
    this.attachedObject.mesh.translateX(this.savedState.totalTranslationX);
  }else if (this.description.action == animationHandler.actionTypes.OBJECT.TRANSLATE_Y){
    this.params.totalTranslationY = this.savedState.totalTranslationY;
    this.attachedObject.mesh.translateY(this.savedState.totalTranslationY);
  }else if (this.description.action == animationHandler.actionTypes.OBJECT.TRANSLATE_Z){
    this.params.totalTranslationZ = this.savedState.totalTranslationZ;
    this.attachedObject.mesh.translateZ(this.savedState.totalTranslationZ);
  }
}

Animation.prototype.saveState = function(){
  this.savedState.initialValue = this.initialValue;
  this.savedState.tick = this.tick;
  this.savedState.increaseTick = this.increaseTick;
  if (this.description.action == animationHandler.actionTypes.OBJECT.TRANSLATE_X){
    this.savedState.totalTranslationX = this.params.totalTranslationX;
  }else if (this.description.action == animationHandler.actionTypes.OBJECT.TRANSLATE_Y){
    this.savedState.totalTranslationY = this.params.totalTranslationY;
  }else if (this.description.action == animationHandler.actionTypes.OBJECT.TRANSLATE_Z){
    this.savedState.totalTranslationZ = this.params.totalTranslationZ;
  }
}

Animation.prototype.isInitialValueAssigned = function(){
  return this.hasInitialValue;
}

Animation.prototype.invalidateInitialValue = function(){
  this.hasInitialValue = false;
  if (this.description.action == animationHandler.actionTypes.OBJECT.TRANSLATE_X){
    this.params.totalTranslationX = 0;
  }else if (this.description.action == animationHandler.actionTypes.OBJECT.TRANSLATE_Y){
    this.params.totalTranslationY = 0;
  }else if (this.description.action == animationHandler.actionTypes.OBJECT.TRANSLATE_Z){
    this.params.totalTranslationZ = 0;
  }
}

Animation.prototype.setFinishCallbackFunction = function(callbackFunction){
  this.finishCallbackFunction = callbackFunction;
}

Animation.prototype.export = function(){
  return {
    name: this.name, type: this.type, description: this.description, rewind: this.rewind, repeat: this.repeat
  };
}

Animation.prototype.onFinished = function(){
  animationHandler.onAnimationFinished(this);
  this.isActive = false;
}

Animation.prototype.onStart = function(initialValue){
  this.animationState = ANIMATION_STATE_RUNNING;
  this.initialValue = initialValue;
  this.tick = 0;
  if (this.description.action == animationHandler.actionTypes.OBJECT.EMISSIVE_COLOR){
    this.params.sourceColor.copy(this.attachedObject.getEmissiveColor());
  }else if (this.description.action == animationHandler.actionTypes.TEXT.TEXT_COLOR){
    this.params.sourceColor.copy(this.attachedObject.getColor());
  }else if (this.description.action == animationHandler.actionTypes.TEXT.BACKGROUND_COLOR){
    this.params.sourceColor.copy(this.attachedObject.getBackgroundColor());
  }else if (this.description.action == animationHandler.actionTypes.SPRITE.COLOR){
    this.params.sourceColor.copy(this.attachedObject.mesh.material.uniforms.color.value);
  }else if (this.description.action == animationHandler.actionTypes.TEXT.TYPING){
    this.params.sourceText = this.attachedObject.text;
    this.changeInValue = this.attachedObject.text.length + 1;
    this.attachedObject.setText("");
  }else if (this.description.action == animationHandler.actionTypes.OBJECT.TRANSLATE_X){
    this.params.totalTranslationX = 0;
  }else if (this.description.action == animationHandler.actionTypes.OBJECT.TRANSLATE_Y){
    this.params.totalTranslationY = 0;
  }else if (this.description.action == animationHandler.actionTypes.OBJECT.TRANSLATE_Z){
    this.params.totalTranslationZ = 0;
  }else if (this.description.action == animationHandler.actionTypes.SPRITE.TARGET_POSITION_X){
    this.params.sourcePosition = this.attachedObject.marginPercentX;
  }else if (this.description.action == animationHandler.actionTypes.SPRITE.TARGET_POSITION_Y){
    this.params.sourcePosition = this.attachedObject.marginPercentY;
  }else if (this.description.action == animationHandler.actionTypes.SPRITE.TARGET_ROTATION){
    this.params.sourceRotation = this.attachedObject.mesh.material.uniforms.rotationAngle.value;
  }else if (this.description.action == animationHandler.actionTypes.SPRITE.TARGET_SCALE_X){
    this.params.sourceScale = this.attachedObject.mesh.material.uniforms.scale.value.x;
    if (!(typeof this.attachedObject.fixedWidth == UNDEFINED)){
      this.params.sourceScale = this.attachedObject.fixedWidth
    }
  }else if (this.description.action == animationHandler.actionTypes.SPRITE.TARGET_SCALE_Y){
    this.params.sourceScale = this.attachedObject.mesh.material.uniforms.scale.value.y;
    if (!(typeof this.attachedObject.fixedHeight == UNDEFINED)){
      this.params.sourceScale = this.attachedObject.fixedHeight
    }
  }
  this.increaseTick = true;
  this.isActive = true;
}

Animation.prototype.onRepeat = function(){
  if (this.description.action == animationHandler.actionTypes.OBJECT.TRANSLATE_X){
    this.attachedObject.mesh.translateX(-1 * this.params.totalTranslationX);
    this.params.totalTranslationX = 0;
  }else if (this.description.action == animationHandler.actionTypes.OBJECT.TRANSLATE_Y){
    this.attachedObject.mesh.translateY(-1 * this.params.totalTranslationY);
    this.params.totalTranslationY = 0;
  }else if (this.description.action == animationHandler.actionTypes.OBJECT.TRANSLATE_Z){
    this.attachedObject.mesh.translateZ(-1 * this.params.totalTranslationZ);
    this.params.totalTranslationZ = 0;
  }
}

Animation.prototype.cancelRewind = function(){
  this.increaseTick = true;
}

Animation.prototype.forceRewind = function(){
  this.increaseTick = false;
}

Animation.prototype.update = function(){
  if (this.isFreezed && this.freezeOnFinish){
    this.animationState = ANIMATION_STATE_FROZEN;
    return;
  }
  this.isFreezed = false;
  if (this.tick <= this.totalTimeInSeconds && this.tick >= 0){
    this.params.value = this.updateFunction(this.tick, this.initialValue, this.changeInValue, this.totalTimeInSeconds);
    this.actionFunction(this.params, this.increaseTick);
  }
  if (this.increaseTick){
    this.animationState = ANIMATION_STATE_RUNNING;
    this.tick += STEP;
  }else{
    this.animationState = ANIMATION_STATE_REWINDING;
    this.tick -= STEP;
  }
  if (this.increaseTick && this.tick > this.totalTimeInSeconds){
    if (this.freezeOnFinish){
      this.isFreezed = true;
      return;
    }
    if (this.rewind){
      this.increaseTick = false;
    }else if (!this.repeat){
      this.onFinished();
    }else{
      this.tick = 0;
      if (!this.rewind){
        this.onRepeat();
      }
    }
    if (!this.rewind && this.finishCallbackFunction){
      this.finishCallbackFunction();
    }
  }else if (!this.increaseTick && this.tick <= 0){
    this.increaseTick = true;
    if (!this.repeat){
      this.onFinished();
    }
    if (this.finishCallbackFunction){
      this.finishCallbackFunction();
    }
  }
}

var DelayedExecutionHandler = function(){
  this.INITIAL_OBJECT_POOL_SIZE = 5;
  this.initializePool();
}

DelayedExecutionHandler.prototype.reset = function(){
  this.initializePool();
}

DelayedExecutionHandler.prototype.pushNewElementToPool = function(){
  this.pool.push({
    isAvailable: true, triggerTimeInMS: 0, func: null, repeat: false, delayInMS: 0
  });
}

DelayedExecutionHandler.prototype.prepareElement = function(elem, delayInMS, func, repeat){
  elem.isAvailable = false;
  elem.triggerTimeInMS = performance.now() + delayInMS;
  elem.func = func;
  elem.repeat = repeat;
  elem.delayInMS = delayInMS;
}

DelayedExecutionHandler.prototype.requestDelayedExecution = function(delayInMS, func, repeat){
  for (var i = 0; i < this.pool.length; i ++){
    var curElem = this.pool[i];
    if (curElem.isAvailable){
      this.prepareElement(curElem, delayInMS, func, repeat);
      return i;
    }
  }
  this.pushNewElementToPool();
  this.prepareElement(this.pool[this.pool.length -1], delayInMS, func, repeat);
  return this.pool.length -1;
}

DelayedExecutionHandler.prototype.stopDelayedExecution = function(id){
  var elem = this.pool[id];
  if (elem){
    elem.isAvailable = true;
  }
}

DelayedExecutionHandler.prototype.initializePool = function(){
  this.pool = [];
  for (var i = 0; i < this.INITIAL_OBJECT_POOL_SIZE; i++){
    this.pushNewElementToPool();
  }
}

DelayedExecutionHandler.prototype.tick = function(){
  var now = performance.now();
  for (var i = 0; i < this.pool.length; i ++){
    var curElem = this.pool[i];
    if (!curElem.isAvailable){
      if (now >= curElem.triggerTimeInMS){
        curElem.func();
        if (curElem.repeat){
          curElem.triggerTimeInMS += curElem.delayInMS;
        }else{
          curElem.isAvailable = true;
        }
      }
    }
  }
}

